CREATE TABLE `tcpr_gestioncampanas` (
  `idGestionCampana` bigint NOT NULL AUTO_INCREMENT,
  `idCampana` bigint NOT NULL DEFAULT '0' COMMENT 'codigo de campana',
  `idCliente` bigint NOT NULL DEFAULT '0' COMMENT 'codigo de  maestro',
  `idTipificacion` int NOT NULL DEFAULT '0' COMMENT 'estado de gestion de la campana',
  `idResultado` int NOT NULL DEFAULT '0' COMMENT 'Ultimo resultado de la llamada',
  `idUsuario` bigint NOT NULL DEFAULT '0' COMMENT 'codigo de asesor Asignado',
  `idLlamada` bigint NOT NULL DEFAULT '0' COMMENT 'id de la llamada que genero este estado',
  `idCita` int NOT NULL DEFAULT '0' COMMENT 'id  de la ultima cita',
  `listaPrecio` int NOT NULL DEFAULT '0' COMMENT 'ID lista de precios',
  `idPunto` bigint NOT NULL DEFAULT '1' COMMENT 'codigo de sector de oficina',
