  `idTipoCliente` bigint NOT NULL DEFAULT '1',
  `idAgregador` bigint NOT NULL DEFAULT '1',
  `idCargo` bigint NOT NULL DEFAULT '1',
  `idClasificacionCliente` int DEFAULT '1' COMMENT 'Id clasificacion de cliente',
  `idEjecutivo` int NOT NULL DEFAULT '1',
  `idEstadoCivil` int NOT NULL DEFAULT '1',
  `idProfesion` int NOT NULL DEFAULT '1',
  `fecha_asigna_a_clavesid` datetime NOT NULL DEFAULT '2000-01-01 00:00:00',
  `activo` tinyint NOT NULL DEFAULT '1' COMMENT 'diferente a 0 si se elimino el cliente',
  `idUsuarioCrea` bigint NOT NULL DEFAULT '0' COMMENT 'id de asesor que creo el registro',
  `fechaCrea` datetime NOT NULL DEFAULT '1981-01-07 00:00:00' COMMENT 'fecha de creacion del registro',
  PRIMARY KEY (`idGestionCampana`) USING BTREE,
  UNIQUE KEY `campaña, codigo` (`idCampana`,`idCliente`) USING BTREE,
  KEY `eliminado` (`idLlamada`) USING BTREE,
  KEY `dialogo_codigo` (`idCampana`) USING BTREE,
  KEY `maestro_codigo` (`idCliente`) USING BTREE,
  KEY `status_vta` (`idTipificacion`) USING BTREE,
