CREATE TABLE `tcpr_ordengeneral` (
  `id` bigint NOT NULL AUTO_INCREMENT,
  `idCliente` bigint NOT NULL DEFAULT '0',
  `idMarca` bigint NOT NULL DEFAULT '0' COMMENT 'id de la marca del cliente en nuestra tabla',
  `idMarcaAgregador` text CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL COMMENT 'Guarda la marca que llega desde el agregador',
  `idAgregador` bigint NOT NULL DEFAULT '0',
  `idPunto` bigint NOT NULL DEFAULT '0' COMMENT 'id del punto fisico del cliente',
  `idMultiPunto` bigint unsigned NOT NULL DEFAULT '0' COMMENT 'id del punto virtual del cliente',
  `idOrdenDia` bigint NOT NULL DEFAULT '0' COMMENT 'Guarda el id del numero de orden del dia para pdv',
  `idOrdenGeneral` bigint NOT NULL DEFAULT '0' COMMENT 'id de la orden general de la marca interno',
  `idOrdenNumero` text CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL,
  `nombreCliente` text CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL,
  `empresa` text CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL,
  `identificacion` text CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL,
  `telefonoFijo` varchar(20) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL DEFAULT '0',
  `telefonoCelular` varchar(20) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL DEFAULT '0',
  `idCiudad` bigint NOT NULL,
  `direccion` text CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL,
  `barrio` text CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL,
  `email` text CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL,
  `lat` text CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci COMMENT 'Guarda la latitud de la direccion del cliente',
  `lng` text CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci COMMENT 'Guarda la longitud de la direccion del cliente',
  `nomTipoEntrega` varchar(10) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci DEFAULT 'nd',
  `valorItems` double(15,2) NOT NULL DEFAULT '0.00' COMMENT 'Precio del pedido sin incluír propina ni precio del despacho, solo el precio de los productos',
  `valorDomicilio` double(15,2) NOT NULL DEFAULT '0.00' COMMENT 'Precio del despacho o domicilio',
  `valorPropina` double(15,2) NOT NULL DEFAULT '0.00' COMMENT 'Monto entregado en propina',
  `valorBaseItems` double(15,2) NOT NULL DEFAULT '0.00' COMMENT 'Precio de los productos, antes de los descuentos',
  `valorBaseTotalOrden` double(15,2) NOT NULL DEFAULT '0.00' COMMENT 'Precio total antes de los descuentos',
  `valorDescuento` double(15,2) NOT NULL COMMENT 'Monto del descuento del cupón',
  `valorOrden` double(15,2) NOT NULL DEFAULT '0.00' COMMENT 'Monto del pedido sin incluír propina. La boleta a generar debe ser de este monto',
  `totalOrden` double(15,2) NOT NULL DEFAULT '0.00' COMMENT 'Monto a pagar por el cliente incluyendo la propina',
  `valorIngresaCli` double(15,2) NOT NULL DEFAULT '0.00' COMMENT 'Si es que el medio de pago es "cash", el monto que el cliente indica que va a pagar',
  `idTipoVenta` tinyint NOT NULL DEFAULT '1' COMMENT 'Id del tipo de venta, es un pedido o una cotización',
  `idEstadoOrden` tinyint NOT NULL DEFAULT '0' COMMENT 'id del estado de la orden',
  `idEstadoPos` tinyint NOT NULL DEFAULT '1' COMMENT 'id del estado del POS',
  `idCupon` text CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL COMMENT 'Código del cupón',
  `nombreCupon` text CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL COMMENT 'Nombre del cupón usado',
  `impresion` tinyint NOT NULL DEFAULT '0',
  `impresionLogistica` tinyint NOT NULL DEFAULT '0',
  `idLogistica` int NOT NULL DEFAULT '1',
  `idDomiciliario` bigint NOT NULL DEFAULT '0',
  `factura` text CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci,
  `observaciones` text CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci,
  `idUsuario` bigint unsigned NOT NULL DEFAULT '1' COMMENT 'Usuario que crea la orden',
  `idUsuarioCancel` bigint unsigned NOT NULL DEFAULT '1' COMMENT 'Usuario que cancela la orden',
  `fechaEstado` datetime NOT NULL DEFAULT '1981-01-07 00:00:00' COMMENT 'Fecha y hora de cambio de estado de la orden',
  `fechaFactura` datetime NOT NULL DEFAULT '1981-01-07 00:00:00' COMMENT 'Fecha y hora de la generacion de la factura',
  `fechaOrden` datetime NOT NULL DEFAULT '1981-01-07 00:00:00' COMMENT 'Fecha y hora que llega en la integración del pedido',
  `fechaCrea` datetime NOT NULL DEFAULT '1981-01-07 00:00:00' COMMENT 'Fecha y hora de nuestro insert en la base',
  PRIMARY KEY (`id`) USING BTREE,
  KEY `id` (`id`) USING BTREE,
  KEY `id_punto` (`idPunto`) USING BTREE,
  KEY `nro_orden_general` (`idOrdenGeneral`) USING BTREE,
