  KEY `estado_orden` (`idEstadoOrden`) USING BTREE,
  KEY `FK_id_logistica_in_reg_orden_general` (`idLogistica`) USING BTREE,
  KEY `idEstadoPosINordengeneral` (`idEstadoPos`) USING BTREE,
  KEY `idAgregador` (`idAgregador`) USING BTREE,
  KEY `idMultiPuntoorg` (`idMultiPunto`) USING BTREE,
  KEY `idtipoVentaTog` (`idTipoVenta`) USING BTREE,
