  CONSTRAINT `idEstadoPosINordengeneral` FOREIGN KEY (`idEstadoPos`) REFERENCES `tcpc_estadopos` (`idEstadoPos`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `idMultiPuntoorg` FOREIGN KEY (`idMultiPunto`) REFERENCES `tcpc_puntoventa` (`idMultiPunto`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `idPuntoINordengeneral` FOREIGN KEY (`idPunto`) REFERENCES `tcpc_puntoventa` (`idPunto`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `idTipoVenta` FOREIGN KEY (`idTipoVenta`) REFERENCES `tcpc_tipoventa` (`idTipoVenta`) ON DELETE RESTRICT ON UPDATE RESTRICT,
