		UPDATE cat_login_usuarios SET
			id_ciudad = _id_ciudad,
			lat_lng = _lat_lng
		WHERE id = _id_user;
        
		IF _perfil >= 3 THEN 
			UPDATE reg_cliente SET
				nombre = _nombre_de,
				telefono_fijo = _tel_fijo,
				telefono_celular = _tel_celu,
				direccion = _direccion,
				barrio = _barrio,
                num_identificacion = _nun_identificacion
			WHERE id_usuario = _id_user;
		ELSE 
			UPDATE reg_aliado SET
				nombre = _nombre_de,
				telefono_fijo = _tel_fijo,
				telefono_celular = _tel_celu,
				direccion = _direccion,
				barrio = _barrio,
                num_identificacion = _nun_identificacion
			WHERE id_usuario = _id_user;
		END IF;
        
        SELECT "Datos guardados correctamente." msg, 1 response;
    END IF;
END ;;
