    IN _valor_orden integer,
    IN _valor_propina integer, 
    IN _direccion text, 
    IN _lat_lng text,
    IN _observaciones text,
	IN _metodo_pago integer)
BEGIN
	DECLARE _num_orden_general, _num_orden_dia, _id_logistica, _nro_orden integer;
    DECLARE _unix_time_orden bigint;
    
    #SELECT id_ciudad INTO _id_ciudad FROM geo_ciudad WHERE nombre LIKE concat('%', _name_ciudad, '%') LIMIT 1;
    #SET _id_ciudad = ifnull(_id_ciudad, 1);
    
    SELECT nro_orden_general, nro_orden_dia, id_logistica, unix_time_orden INTO _num_orden_general, _num_orden_dia, _id_logistica, _unix_time_orden 
    FROM cat_punto_venta WHERE id_punto = _id_punto;
    
    SET _num_orden_general = _num_orden_general + 1;
    SET _unix_time_orden =  _unix_time_orden + 86400;
	IF _unix_time_orden < unix_timestamp() THEN 
			UPDATE cat_punto_venta SET
				nro_orden_general = _num_orden_general,
				nro_orden_dia = 1,
				fecha_orden = current_timestamp(),
				unix_time_orden = unix_timestamp()
			WHERE id_punto = _id_punto;
			
            SET _num_orden_dia = 1;
	ELSE 
		SET _num_orden_dia = _num_orden_dia + 1;
        
         UPDATE cat_punto_venta SET
			nro_orden_general = _num_orden_general,
			nro_orden_dia = _num_orden_dia
		WHERE id_punto = _id_punto;
    END IF;
    
    INSERT INTO reg_orden_general (
		id_punto,
		id_cliente,
		nombre_cliente,
		empresa,
		identificacion,
		telefono_fijo,
		telefono_celular,
		id_ciudad,
		direccion,
		barrio,
		email,
		nro_orden_general,
		nro_orden_dia,
		valor_orden,
		valor_propina,
        estado_orden,
        id_logistica,
        factura,
		observaciones,
		fecha_crea
	) VALUES (
		_id_punto,
		_id_usuario,
		_a_nombre_de,
		_a_empresa,
		_nun_identificacion,
		_tel_fijo,
		_tel_celu,
		_id_ciudad,
		_direccion,
		_barrio,
		_correo,
		_num_orden_general,
		_num_orden_dia,
		_valor_orden,
		_valor_propina,
        1,
        _id_logistica,
        0,
		_observaciones,
		current_timestamp()
	);
    
    SET _nro_orden = last_insert_id();
    
    SET _valor_orden = _valor_orden + _valor_propina;
    
    INSERT INTO reg_orden_tipopago ( id_punto,id_tipopago, nro_orden_general, valor, fecha_crea ) 
                            VALUES (_id_punto,_metodo_pago, _num_orden_general, _valor_orden, current_timestamp());
	SELECT 
		"La orden se guardo correctamente" msg, 
        1 response, 
        _num_orden_general num_orden_general,
        _nro_orden nro_orden;
END ;;
