CREATE DEFINER=`integration`@`%` PROCEDURE `impresion_detalle_orden_logistica`(_codrestaurante INT, 
_estado INT, _nro_orden INT)
BEGIN
DECLARE _NameOperador VARCHAR(3);
DECLARE  pedidoOnline VARCHAR(20);
IF _nro_orden > 0 THEN
UPDATE reg_orden_detalle rod SET observacion = 'seleccion'
WHERE valor_unitario=0 AND nro_orden_general=  _nro_orden;
END IF;
SELECT nro_orden_general INTO  pedidoOnline FROM reg_orden_general  WHERE nro_orden_general = _nro_orden;
IF _nro_orden > 0 THEN
		UPDATE  reg_orden_general
		SET direccion = REPLACE( direccion, "    ", " ")
		WHERE  nro_orden_general = _nro_orden;
		
		UPDATE  reg_orden_general
		SET direccion = REPLACE( direccion, "   ", " ")
		WHERE  nro_orden_general = _nro_orden;
		UPDATE  reg_orden_general
		SET direccion = REPLACE( direccion, "  ", " ")
		WHERE  nro_orden_general = _nro_orden;
	
	/*IF EXISTS(SELECT DISTINCT pc.dto_nro FROM calendar c, a_proformas_cabecera pc 
	WHERE 
		c.codigo = pc.cod_cli
	AND telefono LIKE  '%5524691%' 
	AND pc.dto_nro = _nro_orden
	AND codigo IN(SELECT cod_cli FROM a_proformas WHERE STATUS = 2)) THEN
	SET _NameOperador = "ETB";
	ELSE 
	SET _NameOperador = "UNE";
	
	
	END IF;*/
	
	
	END IF;
	
	 
		IF _nro_orden = 0 THEN
		
		
			SELECT rog.nro_orden_general AS pedido
			FROM reg_orden_general rog
			WHERE  rog.id_punto = _codrestaurante
			AND rog.impresion_logistica=0
			GROUP BY rog.nro_orden_general;
		
		ELSE
		
		
 	SELECT rog.nro_orden_dia AS pedido_conce_dia, rog.nro_orden_general AS pedido, '' AS contrato,
	cu.id as codigo, rog.nombre_cliente AS nombre,
        SUBSTRING(rog.direccion,1,24) AS dir1,
        IF(LENGTH(rog.direccion) > 24, SUBSTR(rog.direccion,25,30),'') AS dir2,
	IF(LENGTH(rog.direccion) > 55, SUBSTR(rog.direccion,55,30),'') AS dir3,
	rog.direccion AS direccion, CONCAT(rog.telefono_fijo, '-', rog.telefono_celular) AS telefono, rod.observacion as notas,
	SUBSTR(rod.observacion,1,30) AS notas1_30,
	IF(LENGTH(rod.observacion) > 30, SUBSTR(REPLACE(rod.observacion, "\n",""),31,30),'') AS notas31_60,
	IF(LENGTH(rod.observacion) > 60, SUBSTR(REPLACE(rod.observacion, "\n",""),61,30),'') AS notas61_90,
	IF(LENGTH(rod.observacion) > 90, SUBSTR(REPLACE(rod.observacion, "\n",""),91,30),'') AS notas91_120,
	IF(LENGTH(rod.observacion) > 120, SUBSTR(REPLACE(rod.observacion, "\n",""),121,30),'') AS notas121_150,
	rod.sku_item AS cod_producto,
	IF(rod.valor_unitario=0, CONCAT('  * ',  REPLACE(rod.nombre,"-1","")),
	CONCAT('  ',rod.sku_item, ' ' , rod.nombre, '  $ ',CAST(rod.valor_unitario AS DECIMAL(20))) )  AS producto,
	-- MID(CONCAT(rod.sku_item, ' ' , rod.nombre, ' $ ' , CAST(rod.valor_unitario AS DECIMAL(20)) ),1,29) AS producto1,
	CONCAT(rod.cantidad, '      ', rod.nombre)   AS producto1,
	MID(CONCAT(rod.sku_item, ' ' , rod.nombre , ' $ ', CAST(rod.valor_unitario AS DECIMAL(20)) ),30,60) AS producto2,
        IF(LENGTH(rod.observacion)>2,CONCAT("[", rod.observacion, "]",""),"") AS notaprod,
	IF(rod.valor_unitario=0 AND TRIM(REPLACE(rod.observacion,"\n"," ")) = 'seleccion','',CONCAT(CONVERT(rod.cantidad, CHAR), "  ")) AS cantidad,
        rod.valor_unitario AS precio, rog.id_punto AS cod_sector,cpdv.nombre  AS sector,  CONVERT(NOW(), CHAR) AS fecha,
	'Midomicilio.com.co' AS usuario,CONVERT(LEFT(rog.fecha_crea, 10), CHAR) fecha_pedido,
	rog.nro_orden_dia AS Consecutivo,'ORDEN' AS cabecera, rod.observacion AS notasproforma,
	gc.nombre AS ciudad,rog.barrio,"OrdenOnline" AS origen,'' AS referencia,'' AS referencia2,
	rog.empresa,rog.identificacion AS ced_ident,
	'Pendiente' AS telf_ofi_ext, 'Pendiente' AS bono1, 'Pendiente' AS bono2 ,
        'ACTIVO' AS esactivo, 1 AS caja_numero, rog.nro_orden_general AS pedidoOnline,"AFILIADO" AS esafiliado,rog.valor_propina as propina
        ,rod.valor_total AS precio,cl.nombre AS logistica
        FROM cat_login_usuarios cu
 	INNER JOIN reg_orden_general rog ON cu.id = rog.id_cliente
	INNER JOIN reg_orden_detalle rod ON rod.nro_orden_general = rog.nro_orden_general
	INNER JOIN cat_punto_venta cpdv ON cpdv.id_punto = rog.id_punto
	INNER JOIN geo_ciudad gc ON rog.id_ciudad= gc.id_ciudad
	INNER JOIN cat_logistica cl ON cl.id=rog.id_logistica
	WHERE rog.nro_orden_general = _nro_orden
	ORDER BY rod.id;
END IF;
END ;;
