CREATE DEFINER=`integration`@`%` PROCEDURE `lcq_impresion_detalle`(_codrestaurante INT, 
_estado INT, 
_nro_pedido INT)
BEGIN
DECLARE _NameOperador VARCHAR(3);
DECLARE Resolucion VARCHAR(300);
DECLARE FACTURA_AUTO INT; 
 
 
 
SELECT CONCAT('Resolucion DIAN   ', numero_resolucion, ' \n  ', 
'Fecha Resoluciòn:  ',  fecha_resolucion, 
'\n',
 'Fact. Autorizadas:  D', CODIGO_PUNTO, '-', valor_inicial, '  \n  ', 'D', CODIGO_PUNTO,'-', valor_final, " \n ") 
 INTO Resolucion
FROM CAT_DIAN  WHERE CODIGO_PUNTO = _codrestaurante;
SELECT imprime_factura_auto INTO FACTURA_AUTO FROM p_sector WHERE id_p_sector = _codrestaurante LIMIT 1;
IF _nro_pedido > 0 THEN
	UPDATE  maestro m INNER JOIN a_proformas a ON m.codigo = a.cod_cli
	INNER JOIN a_pro_pago ap ON ap.factura = a.dto_nro
	SET m.direc_ofi = REPLACE( m.direc_ofi, "    ", " ")
	WHERE  a.dto_nro = _nro_pedido;
	UPDATE  maestro m INNER JOIN a_proformas a ON m.codigo = a.cod_cli
	INNER JOIN a_pro_pago ap ON ap.factura = a.dto_nro
	SET m.direc_ofi = REPLACE( m.direc_ofi, "   ", " ")
	WHERE  a.dto_nro = _nro_pedido;
	UPDATE  maestro m INNER JOIN a_proformas a ON m.codigo = a.cod_cli
	INNER JOIN a_pro_pago ap ON ap.factura = a.dto_nro
	SET m.direc_ofi = REPLACE( m.direc_ofi, "  ", " ")
	WHERE  a.dto_nro = _nro_pedido;
	
	
	UPDATE  a_proformas_cabecera   
	INNER JOIN a_pro_pago_forma   ON a_proformas_cabecera.dto_nro = a_pro_pago_forma.dto_nro
	INNER JOIN a_pro_pago  ON a_proformas_cabecera.dto_nro = a_pro_pago.factura
	INNER JOIN dom_tdl_gestion ON a_proformas_cabecera.dto_nro = dom_tdl_gestion.dto_nro
	SET a_proformas_cabecera.estado = 2
	WHERE a_proformas_cabecera.dto_nro = _nro_pedido AND a_proformas_cabecera.estado  = 1;	
		
	
	        UPDATE  A_PROFORMAS_cabecera  SET direccion_pedido = REPLACE(direccion_pedido, "\n"," ")
		WHERE DTO_NRO = _nro_pedido;
		UPDATE  A_PROFORMAS_cabecera  SET direccion_pedido = REPLACE(direccion_pedido, "     "," ")
		WHERE DTO_NRO = _nro_pedido ;
		UPDATE  A_PROFORMAS_cabecera  SET direccion_pedido = REPLACE(direccion_pedido, "    "," ")
		WHERE DTO_NRO = _nro_pedido;
	        UPDATE  A_PROFORMAS_cabecera  SET direccion_pedido = REPLACE(direccion_pedido, "   "," ")
		WHERE DTO_NRO = _nro_pedido;
		
	END IF;
		IF _nro_pedido = 0 THEN
			SELECT a.dto_nro AS pedido, a.procesado, FACTURA_AUTO
			FROM maestro m 
			INNER JOIN a_proformas a ON m.codigo = a.cod_cli
			INNER JOIN a_proformas_cabecera apc ON apc.dto_nro=a.dto_nro
			INNER JOIN a_pro_pago ap ON ap.factura = a.dto_nro
			WHERE  a.sector = _codrestaurante 
			AND  ap.Estado_Despacho = 1
			AND apc.estado = 2
			AND a.procesado IN( 0,2)
			GROUP BY a.dto_nro;
		
		ELSE
 			SELECT apc.id_p_sector AS punto, apc.factura_prefijo, apc.factura_numero,  
 			ap.id AS pedido_conce_dia, a.dto_nro AS pedido, contrato,
			m.codigo, 
			IF(LENGTH(m.empresa)>3,m.empresa, 
			CONCAT(m.nombre , ' ' , m.apellido))  AS nombre ,
			CONCAT(m.nombre , ' ' , m.apellido)  AS contacto,
			SUBSTRING(CONCAT(apc.direccion_pedido, ' ',m.dir5),1,31) AS dir1,
			-- SUBSTRING(apc.direccion_pedido,1,31) AS dir1,
			IF(LENGTH(apc.direccion_pedido) > 31, SUBSTR(apc.direccion_pedido,31,36),'') AS dir2,
			IF(LENGTH(apc.direccion_pedido) > 58, SUBSTR(apc.direccion_pedido,67,36),'') AS dir3,
			IF(LENGTH(apc.direccion_pedido) > 75	, SUBSTR(apc.direccion_pedido,103,36),'') AS dir4,			
			
			
			/*SUBSTRING(m.direc_ofi,1,30) AS dir1,
			IF(LENGTH(m.direc_ofi) > 30, SUBSTR(m.direc_ofi,31,36),'') AS dir2,
			IF(LENGTH(m.direc_ofi) > 58, SUBSTR(m.direc_ofi,67,36),'') AS dir3,
			IF(LENGTH(m.direc_ofi) > 75	, SUBSTR(m.direc_ofi,103,36),'') AS dir4,*/
			
			m.direc_ofi AS direccion, 
			CONCAT(m.telf_ofi, '-', m.celular) AS telefono
			, ap.notas,
			SUBSTR(ap.notas,1,30) AS notas1_30  
			, IF(LENGTH(ap.notas) > 30, SUBSTR(ap.notas,31,30),'') AS notas31_60 
			, IF(LENGTH(ap.notas) > 60, SUBSTR(ap.notas,61,30),'') AS notas61_90 
			, IF(LENGTH(ap.notas) > 90, SUBSTR(ap.notas,91,30),'') AS notas91_120 
			, IF(LENGTH(ap.notas) > 120, SUBSTR(ap.notas,121,30),'') AS notas121_150,
			a.codigo AS cod_producto, a.producto_tipo,
			
			CONCAT(a.nombre, '   ', a.cantidad, '  $ ',CAST((a.precio * a.cantidad ) AS DECIMAL(20)))   AS producto,
		        CONCAT(a.cantidad, '  ', MID(a.nombre,1,30))  AS producto1, 
			MID(CONCAT(a.nombre , ' $ ', CAST(a.precio AS DECIMAL(20)) ),30,60) AS producto2, 
			 
		
			IF(LENGTH(a.nota)>2,CONCAT("",  SUBSTRING(REPLACE(a.nota, "\n"," "),1,39), "",""),"") AS notaprod,
			
			IF(a.precio=0 AND TRIM(REPLACE(a.nota,"\n"," ")) = 'seleccion','',CONCAT(CONVERT(a.cantidad, CHAR), "  ")) AS cantidad, 
			IF(a.producto_tipo='SEL',      CONCAT( " ", a.cantidad , '  ', a.nombre),'') AS producto_seleccion,
	                IF(a.producto_tipo='SEL', '',  CONCAT("	 ",a.cantidad , ' X ', CAST(a.precio  AS DECIMAL(20))))    AS precio_producto_p,
	                
	                
	                a.precio, a.sector AS cod_sector,  s.nombre  AS sector 
			 ,  CONVERT(NOW(), CHAR) AS fecha, 
			 
			 CONCAT(us.nombre, IF(apc.codigo_tercero >0, ' /WEB',' /CAL')) AS usuario
		          ,CONVERT(LEFT(a.crea_fecha, 10), CHAR) AS  fecha_pedido
			  ,CONVERT(a.crea_hora, CHAR) AS hora_pedido
			  ,a.dto_nro_lista AS Consecutivo, 
			  aps.nombre  AS cabecera, a.nota AS notasproforma
				,pc.nombre AS ciudad
				,m.ofi_barrio, 
			        IF(TRIM(ap.referencia) = 'ND', '',MID(ap.referencia,1,30)) AS referencia,
				IF(TRIM(ap.referencia) = 'ND', '',MID(ap.referencia,30,68)) AS referencia2,
				m.empresa,
			       m.ced_ident,
				m.telf_ofi_ext, m.dir4 AS bono1, m.dir5 AS bono2  ,  
				IF(m.conducta11 = 1 , "ACTIVO", "") AS esactivo, caja_numero,
				 CAST(apc.total_con_iva AS DECIMAL(20)) AS total_con_iva,
				ROUND(apc.total_con_iva /1.08,0) AS antes_de_iva, ROUND(apc.total_con_iva - (apc.total_con_iva /1.08)) AS IVA,
				-- apt.nombre as formapago, -- , apf.valor_recibido,
				--  cast((apf.valor_recibido-apc.total_con_iva) AS DECIMAL(20)) as cambiode,
				apc.factura_fecha,  apc.estado AS estado_pedido,
				
				ac.numero_telefono AS telefono_ciudad,
		 		SUBSTR(apc.notas,1,30) AS nota_cabecera1,
		 		SUBSTR(apc.notas,31,60) AS nota_cabecera2,
		 		SUBSTR(apc.notas,61,90) AS nota_cabecera3,
		 		
		 		SUBSTR(ap.referencia,1,30) AS transferencia1,
				 SUBSTR(ap.referencia,31,60) AS transferencia2,
				 SUBSTR(ap.referencia,61,90) AS transferencia3,
		 		
		 		 dian.numero_resolucion,CONVERT(dian.fecha_resolucion, CHAR) AS fecha_resolucion,
		 		 dian.valor_inicial, dian.valor_final, apc.usuario_motorizado, 
		 		 IF(usM.nombre IS NULL, "SERVI COURIER",usM.nombre ) AS nombre_motorizado
 				FROM maestro m 
 				INNER JOIN a_proformas_cabecera  apc ON m.codigo = apc.cod_cli
 				-- INNER JOIN A_PRO_PAGO_FORMA  apf ON apc.dto_nro = apf.dto_nro
 			        --	INNER JOIN a_pro_pago_tipos  apt ON apf.tipo = apt.id
				INNER JOIN a_proformas a ON apc.dto_nro = a.dto_nro
				INNER JOIN a_pro_pago ap ON ap.factura = a.dto_nro 
				INNER JOIN p_sector s ON s.id_p_sector = a.sector
				INNER JOIN  usuariosistema us ON us.id = a.crea_usuario
				INNER JOIN a_proformas_status aps ON a.status = aps.id
				INNER JOIN p_ciudad pc ON m.ofi_ciudad = pc.id
				INNER JOIN medios med  ON m.medio = med.medio
				INNER JOIN CAT_DIAN dian ON apc.id_p_sector = dian.CODIGO_PUNTO
				LEFT JOIN  usuariosistema usM ON usM.id = apc.Usuario_motorizado
				 LEFT JOIN areas_ciudades ac ON ac.codigo_marca = s.id_area AND ac.codigo_ciudad = s.cod_ciudad
			WHERE
				ap.factura IN(SELECT dto_nro FROM a_pro_pago_forma) AND 
				a.dto_nro  = _nro_pedido  ORDER BY a.id;
		END IF;
		END ;;
