CREATE DEFINER=`integration`@`%` PROCEDURE `notification_all`(IN _asunto text,
	IN _contenido text)
BEGIN
  DECLARE _iduser integer;
  DECLARE fin BOOLEAN DEFAULT FALSE;
  DECLARE c_notificaciones CURSOR FOR SELECT id FROM cat_login_usuarios;
  DECLARE CONTINUE HANDLER FOR NOT FOUND SET fin = TRUE;
  OPEN c_notificaciones;
	loop_notifica: LOOP
		FETCH c_notificaciones INTO _iduser;
		IF fin THEN
			LEAVE loop_notifica;
		END IF;
    
		INSERT INTO reg_notificaciones (
			propietario,
			titulo,
			contenido
		) VALUES (
			_iduser,
			_asunto,
			_contenido
		);
   
	END LOOP;
  CLOSE c_notificaciones;
  
  SELECT "Se envío el mensaje a todos correctamente!" msg, 1 response;
END ;;
