CREATE DEFINER=`integration`@`%` PROCEDURE `notification_save`(IN _iduser integer,
	IN _asunto text,
	IN _contenido text)
BEGIN
	DECLARE _device, _token text;
    DECLARE _id_noty, _noty_total integer;
    
	SELECT device, token INTO _device, _token FROM cat_login_usuarios WHERE id = _iduser;
    SELECT count(*) INTO _noty_total FROM reg_notificaciones WHERE propietario = _iduser AND visto = 0;
	INSERT INTO reg_notificaciones (
		propietario,
		titulo,
		contenido
	) VALUES (
		_iduser,
		_asunto,
		_contenido
	);
    
    SET _id_noty = last_insert_id();
	SELECT 
		 1 response,
        "La notificación fue enviada de correctamente" msg,
		_id_noty id_noty,
        _noty_total noty_total,
        _device device,
        _token token;
END ;;
