CREATE DEFINER=`integration`@`%` PROCEDURE `producto_activar`(IN	_id_punto integer,
    IN  _id_usuario integer,
    IN 	_direccion_ip text,
	IN	_id_producto integer)
BEGIN
	DECLARE _perfil_usuario, _nombre_usuario, _sku_item, _nombre text;
	# Obtiene la información del usuario 
	SELECT 
		(SELECT descripcion FROM cat_perfil WHERE id_perfil = idperfil),
		(SELECT nombre FROM reg_aliado WHERE id_usuario = id)
	INTO 
		_perfil_usuario,
		_nombre_usuario
	FROM cat_login_usuarios WHERE id = _id_usuario;
    
    # Obtiene la información del producto
	SELECT 
		sku_item,
		nombre
	INTO 
		_sku_item,
		_nombre
	FROM items WHERE id_item = _id_producto;
    
	UPDATE items SET
		inactivo = 0,
		fecha_inactivo = current_timestamp()
	WHERE id_item = _id_producto;
            
    # Guarda en el log los detalles de la eliminacion
	INSERT INTO items_log (
		id_punto,
		tipo,
		id_usuario,
		detalle_user,
		id_item,
		sku_item,
		titulo,
		detalle,
		direccion_ip
	) VALUES (
		_id_punto,
		"Activar",
		_id_usuario,
		concat("Ip: ", _direccion_ip, " \\nId: ", _id_usuario, " \\nPeril: ", _perfil_usuario, " \\nNombre: ", _nombre_usuario),
		0,
		0,
		concat("Producto: ", _nombre),
		concat("Producto\\n - Código: ", _sku_item, " \\n - Nombre: ", _nombre),
		_direccion_ip
	);
   	SELECT concat("El producto ", _nombre, " fue activado correctamente!") msg, 1 response;
END ;;
