    	SELECT "Producto creado correctamente" msg, 1 response;
    ELSE 
		IF _is_new = 1 THEN
			SELECT concat("El código '", _sku_item, "' esta asignado a ", _nombre_old)  msg, -1 response;
        ELSE 
			UPDATE items SET
				nombre = _nombre,
				descripcion = _descripcion,
				id_categoria = _id_categoria,
				impuesto = _impuesto,
