CREATE DEFINER=`integration`@`%` PROCEDURE `producto_agrega_seleccion`(IN	_id_punto integer,
    IN  _id_usuario integer,
    IN 	_direccion_ip text,
    
    IN  _sku_padre text,
	IN	_sku_seleccion text,
	IN	_tipo_seleccion text,
	IN	_precio integer,
    IN	_por_defecto integer,
    IN  _adicional integer)
BEGIN
	DECLARE _id_item, _id_seleccion integer;
    DECLARE _perfil_usuario, _nombre_usuario, _nombre, _nombre_seleccion, _log_adicional text;
    
    # verifica si el sku ya esta en uso 
    SELECT id_item, nombre INTO _id_item, _nombre FROM items 
    WHERE sku_item = _sku_padre
    AND inactivo = 0;
    
    SET _id_item = ifnull(_id_item, 0);
    
    IF _id_item = 0 THEN
		SELECT concat("El producto con el código '", _sku_padre, "' no se ha creado o fue eliminado")  msg, -1 response;
	ELSE 
		# verifica si el producto ya tiene esta seleccion 
		SELECT id INTO _id_seleccion 
        FROM items_seleccion WHERE sku_item = _sku_padre 
        AND sku_item_seleccion = _sku_seleccion
        AND inactivo = 0;
        
        SET _id_seleccion = ifnull(_id_seleccion, 0);
		IF _id_seleccion != 0 THEN
			SELECT concat("El plato '", _nombre, "' ya tiene esta esta selección \nprueba con otra")  msg, -1 response;
		ELSE
			# Si es por defecto desmarca las demas
			IF _por_defecto = 1 THEN 
				UPDATE items_seleccion SET
					por_defecto = 0
				WHERE sku_item = _sku_padre
                AND sku_item_seleccion = _sku_seleccion;
            END IF;
            
			# Guarda la información de la seleccion
			INSERT INTO items_seleccion (
				sku_item,
				sku_item_seleccion,
				tipo_seleccion,
				precio,
				por_defecto,
				adicional
			) VALUES (
				_sku_padre,
				_sku_seleccion,
				_tipo_seleccion,
				_precio,
				_por_defecto,
				_adicional
			);
    
			# Obtiene la información del usuario que agrega o edita el producto
			SELECT 
				(SELECT descripcion FROM cat_perfil WHERE id_perfil = idperfil),
				(SELECT nombre FROM reg_aliado WHERE id_usuario = id)
			INTO 
				_perfil_usuario,
				_nombre_usuario
			FROM cat_login_usuarios WHERE id = _id_usuario;
            
            #obtiene el tipo de seleccion y lo guarda en la variable _nombre_seleccion
            SELECT nombre_seleccion INTO _nombre_seleccion FROM cat_seleccion WHERE id_seleccion = _tipo_seleccion;
            
            IF _adicional = 1 THEN 
				SET _log_adicional = concat("\\n - Adicional de $", _precio);
            ELSE 
				SET _log_adicional = "\\n - Parte del plato principal";
            END IF;
    
			# Guarda información del usuario que creo la seleccion
			INSERT INTO items_log (
				id_punto,
				tipo,
				id_usuario,
				detalle_user,
				id_item,
				sku_item,
				titulo,
				detalle,
				direccion_ip
			) VALUES (
				_id_punto,
				"Crea",
				_id_usuario,
				concat("Ip: ", _direccion_ip, " \\nId: ", _id_usuario, " \\nPeril: ", _perfil_usuario, " \\nNombre: ", _nombre_usuario),
				_id_item,
				_sku_padre,
				concat("Selección: ", _nombre_seleccion),
				concat("Selección\\nProducto padre \\n - Código: ", _sku_padre, " \\n - Nombre: ", _nombre, 
						"\\nProducto selección \\n - Código: ", _sku_seleccion, " \\n - Nombre: ", (SELECT nombre FROM items WHERE sku_item = _sku_seleccion), 
                        " \\n - Agregado como: '", _nombre_seleccion, "' ", _log_adicional),
				_direccion_ip
			);
	
			SELECT "Selección agregada correctamente" msg, 1 response;
		END IF;
    END IF;
END ;;
DELIMITER ;
