CREATE DEFINER=`integration`@`%` PROCEDURE `producto_categoria`(IN	_id_punto integer,
    IN  _id_aliado integer,
    IN  _id_usuario integer,
    IN 	_direccion_ip text,
	IN	_id_cat integer,
    IN	_orden integer,
	IN	_nombre_cat text)
BEGIN
	DECLARE _detalle_log, _tipo, _perfil_usuario, _nombre_usuario text;
	# Obtiene la información del usuario que agrego el producto
	SELECT 
		(SELECT descripcion FROM cat_perfil WHERE id_perfil = idperfil),
		(SELECT nombre FROM reg_aliado WHERE id_usuario = id)
	INTO 
		_perfil_usuario,
		_nombre_usuario
	FROM cat_login_usuarios WHERE id = _id_usuario;
	IF _id_cat = 0 THEN
		# crea nueva categoria
		INSERT INTO items_categorias (
			nombre,
            orden,
            id_aliado
		) VALUES (
			_nombre_cat,
            _orden,
            _id_aliado
		);
        
        SET _id_cat = last_insert_id();
		# Detalle de cambio
		SET _tipo = "Crea";
		SET _detalle_log = concat("Categoria \\n - Id: ", _id_cat, " \\n - Orden: ", _orden, " \\n - Nombre: ", _nombre_cat);
	
	ELSE 
        # Detalle de cambio
		SET _tipo = "Modifica";
        SET _detalle_log = concat("Categoria \\n - Id: ", _id_cat, " \\n - Orden: ", _orden, " \\n - Nombre: ", _nombre_cat, " \\n - Nombre anterio: ", (SELECT nombre FROM items_categorias WHERE id_categoria = _id_cat));
    
		# actualiza nombre de la categoria
		UPDATE items_categorias SET
			nombre = _nombre_cat,
            orden = _orden
		WHERE id_categoria = _id_cat;
        
    END IF;
	# Guarda información del usuario que creo el producto
	INSERT INTO items_log (
		id_punto,
		tipo,
		id_usuario,
		detalle_user,
		id_item,
		sku_item,
        titulo,
		detalle,
		direccion_ip
	) VALUES (
		_id_punto,
		_tipo,
		_id_usuario,
		concat("Ip: ", _direccion_ip, " \\nId: ", _id_usuario, " \\nPeril: ", _perfil_usuario, " \\nNombre: ", _nombre_usuario),
		0,
		0,
        concat("Categoría: ", _nombre_cat),
		_detalle_log,
		_direccion_ip
	);
	SELECT "La categoria se guardo correctamente" msg, 1 response;
END ;;
