		0,
        concat("Categoría: ", _nombre_cat),
		concat("Categoria \\n - Id: ", _id_cat, " \\n - Nombre: ", _nombre_cat),
		_direccion_ip
	);
    
    
    # Inactiva todos los productos relacionados con la categoria 
    OPEN c_productos;
		loop_productos: LOOP
			FETCH c_productos INTO _id_item, _sku_item, _nombre, _id_categoria;
			IF fin THEN
				LEAVE loop_productos;
			END IF;
        
			UPDATE items SET
				inactivo = 0,
				fecha_inactivo = current_timestamp()
			WHERE id_item = _id_item;
            
            # Guarda en el log los detalles de la eliminacion
