BEGIN
	-- DECLARE _idPunto INT;
	DECLARE _nroOrdenGeneral INT;
	DECLARE _idUsuario INT;
	DECLARE _idCliente INT;
	
			-- SELECT idPunto INTO _idPunto FROM regordengeneral WHERE idOrdenNumero = _orderId;
