			UPDATE tcpr_trazasdia
			SET fechaFin= NOW()
			WHERE idOrdenGeneral = _nroOrdenGeneral AND fechaFin ='2000-01-01 00:00:00';
			
			IF NOT EXISTS(SELECT idOrdenGeneral
			FROM tcpr_trazas 
