			(_idPunto,_idCliente, _nroOrdenGeneral, '7', _idUsuario, NOW(),NOW(), 'Cancelado Por RappiV2');
			
			INSERT INTO tcpr_trazasdia 
			(idPunto,idCliente,idOrdenGeneral, idTraza, idUsuario, fechaInicio,fechaFin, observaciones)
			VALUES
			(_idPunto,_idCliente, _nroOrdenGeneral, '7', _idUsuario, NOW(),NOW(), 'Cancelado Por RappiV2');
	
END IF;
	SELECT 'OK';
	
	
