CREATE DEFINER=`integration`@`%` PROCEDURE `rappiv2_valida_pedidos`(_idPunto VARCHAR(10),
 _idOrdenNumero VARCHAR(30),
 _maxValorOrden VARCHAR(100),
 _FormaPago VARCHAR(100))
BEGIN
DECLARE _codigoResultado INT DEFAULT 0;   
DECLARE _resultado TEXT DEFAULT '';
DECLARE _fallido INT DEFAULT 0;
DECLARE _idMarca VARCHAR(10) DEFAULT '';
DECLARE _existe INT DEFAULT 0;
	-- ---------------------------------------------------------------------------------------------------------------------
	-- Se forza _idMarca porque no se pidio como parametro de entrada
	SELECT idMarca INTO _idMarca FROM tcpc_puntoventa WHERE idPunto = _idPunto LIMIT 1;
	
	-- Validamos si el punto existe
	IF NOT EXISTS(SELECT idPunto FROM tcpc_puntoventa WHERE idPunto = _idPunto AND activo = 1) THEN
		SET _codigoResultado = -7;
			SET _resultado = "El punto de venta no existe en la alianza.";
			SET _fallido = 0;
		END IF;
	-- Validamos si la orden existe
	IF _codigoResultado = 0 THEN
		IF EXISTS(SELECT idOrdenGeneral FROM tcpr_ordengeneral WHERE idOrdenNumero = _idOrdenNumero ) THEN 
			SET _codigoResultado = -8;
			SET _resultado = CONCAT("Ya existe este pedidos con el numero de orden: ", _idOrdenNumero);
			SET _fallido = 0;
		END IF;
	END IF;
	-- Validamos el total de la orden, para que un pedido se pare si llega por encima de ese valor
	IF _codigoResultado = 0 THEN
		IF EXISTS (SELECT idPunto FROM tcpc_puntoventa WHERE idPunto = _idPunto AND pedidoMaximo < _maxValorOrden) THEN
			SET _codigoResultado = -9;
			SET _resultado = CONCAT('El total de la orden supera el valor maximo para enviar al punto.');
			SET _fallido = 0;
		END IF;
	END IF;
	
		-- Validamos si la forma de pago existe
		IF _codigoResultado = 0 THEN
		IF NOT EXISTS(SELECT nombre FROM  tcpc_tipopago  WHERE tipoAlias = _FormaPago AND idTipoPago != 0) THEN
			SET _codigoResultado = -10;
			SET _Resultado = CONCAT("La forma de pago enviada, no existe en la lista. ",_FormaPago);
		END IF;
	END IF;
	
	-- ---------------------------------------------------------------------------------------------------------------------
	SELECT _codigoResultado AS codigoResultado, _resultado AS resultado, _fallido AS fallido;
	-- ---------------------------------------------------------------------------------------------------------------------
END ;;
