BEGIN
	DECLARE _codigoResultado INT DEFAULT 0;   
	DECLARE _resultado TEXT DEFAULT '';
	DECLARE _fallido INT DEFAULT 0;
	DECLARE	_listaPrecio INT(10) DEFAULT 0;
	-- ----------------------------------------------------------------------------------------------------
	
