	  `skuSeleccion` VARCHAR(20) NOT NULL DEFAULT '',
	  `PrecioSeleccion` DOUBLE(15,2) UNSIGNED NOT NULL DEFAULT '0',

	  PRIMARY KEY  (`id`),
 	  KEY `skuItem` (`skuItem`),
 	  KEY `skuSeleccion` (`skuSeleccion`)
	) ENGINE=INNODB DEFAULT CHARACTER SET =utf8 COMMENT='tabla temporal con modelo de productos y otros parametros';
	
	
	IF NOT EXISTS(SELECT skuItem FROM tcpc_items WHERE skuItem = _skuItem) THEN
		SET _codigoResultado = -12;
		SET _Resultado = CONCAT("El SKU no existe Cod. SKU: (" , _skuItem, ")") ;
	END IF;
	
	/*IF _codigoResultado = 0 THEN
			-- Traemos la lista de precio que tiene el punto
			SELECT listaPrecio INTO _listaPrecio FROM catpuntoventa WHERE idPunto = _idPunto;		
					-- validamos si el sku existe en la lista de precio que tiene el punto
		IF NOT EXISTS(SELECT skuItem FROM itemslistaprecio WHERE skuItem = _skuItem AND listaPrecio = _listaPrecio AND activo = 1) THEN
			SET _codigoResultado = -13;
			SET _Resultado = CONCAT("Producto inactivo o no existe en el punto Cod. SKU: (" , _skuItem, ")") ;
		END IF;
	END IF;*/
