			SELECT listaPrecio INTO _listaPrecio FROM tcpc_puntoventa WHERE idPunto = CAST(_idPunto AS CHAR) LIMIT 1;
		-- ------------------------
	-- crea producto principal
		INSERT INTO modelo
			   (idMarca,idPunto,tipoSku,skuItem,itemNombre,precioSku)
					SELECT _idMarca,_idPunto,'PRI', it.skuItem, it.itemNombre,IF(ilp.precioBruto IS NULL,99999, ilp.precioBruto)
					FROM tcpc_items it LEFT JOIN tcpr_itemslistaprecio ilp ON (it.skuItem = ilp.skuItem AND ilp.activo = 1 AND ilp.listaPrecio= _listaPrecio)
					WHERE it.skuItem= _skuItem;
		-- -------------------------
		-- crea selecciones que tiene el producto principal
		INSERT INTO modelo
				(idMarca,idPunto,tipoSku,skuItem,    skuSeleccion, itemNombre,    PrecioSeleccion)
					SELECT _idMarca,_idPunto,'SEL',prod.skuItem, relopc.skuSeleccion, opc.itemNombre, IF(pre.precioBruto IS NULL,0,pre.precioBruto) AS precioSeleccion 	
					FROM tcpc_items prod
