CREATE DEFINER=`integration`@`%` PROCEDURE `rml_ventas_pormes`(_fecha_ini VARCHAR(10), _fecha_fin VARCHAR(10))
BEGIN
SELECT ve.Mes,ve.id_punto,ve.Punto,FORMAT(ve.pedidos,0) AS Ordenes_Efectivas,FORMAT(ve.venta_mes,0)  AS Venta_Efectiva,
IF(FORMAT(vc.Pedidos_cancelados,0) IS NULL,0,FORMAT(vc.Pedidos_cancelados,0)) AS Ordenes_canceladas,
IF(FORMAT(vc.Venta_cancelada,0) IS NULL,0,FORMAT(vc.Venta_cancelada,0)) AS Venta_cancelada ,
FORMAT(SUM(ve.pedidos+IF(FORMAT(vc.Pedidos_cancelados,0) IS NULL,0,FORMAT(vc.Pedidos_cancelados,0))),0) AS Total_pedidos,
FORMAT(SUM(ve.venta_mes+IF(FORMAT(vc.Venta_cancelada,0) IS NULL,0,FORMAT(vc.Venta_cancelada,0))),0) AS Total_Venta,
FORMAT((ve.venta_mes/ve.pedidos),0) AS Promedio
FROM (
SELECT MONTH(apc.fecha_crea) AS Numes,MONTH(apc.fecha_crea),CASE WHEN MONTH(apc.fecha_crea) = 1 THEN "Enero" WHEN MONTH(apc.fecha_crea) = 2 THEN "Febrero"
WHEN MONTH(apc.fecha_crea) = 3 THEN "Marzo" WHEN MONTH(apc.fecha_crea) = 4 THEN "Abril"
WHEN MONTH(apc.fecha_crea) = 5 THEN "Mayo" WHEN MONTH(apc.fecha_crea) = 6 THEN "Junio"
WHEN MONTH(apc.fecha_crea) = 7 THEN "Julio" WHEN MONTH(apc.fecha_crea) = 8 THEN "Agosto"
WHEN MONTH(apc.fecha_crea) = 9 THEN "Septiembre" WHEN MONTH(apc.fecha_crea) = 10 THEN "Octubre"
WHEN MONTH(apc.fecha_crea) = 11 THEN "Noviembre" WHEN MONTH(apc.fecha_crea) = 12 THEN "Diciembre"
ELSE "Esto no es un Mes" END AS Mes,apc.id_punto,
COUNT(1) AS Pedidos,SUM(valor_orden) AS Venta_mes,cpdv.nombre AS punto
FROM reg_orden_general apc
INNER JOIN cat_punto_venta cpdv ON cpdv.id_punto = apc.id_punto
WHERE estado_orden=1  AND LEFT(apc.fecha_crea,10) BETWEEN _fecha_ini AND _fecha_fin GROUP BY LEFT(fecha_crea,7),apc.id_punto) ve
LEFT JOIN
(SELECT MONTH(apc.fecha_crea) AS Numes1,CASE WHEN MONTH(apc.fecha_crea) = 1 THEN "Enero" WHEN MONTH(apc.fecha_crea) = 2 THEN "Febrero"
WHEN MONTH(apc.fecha_crea) = 3 THEN "Marzo" WHEN MONTH(apc.fecha_crea) = 4 THEN "Abril"
WHEN MONTH(apc.fecha_crea) = 5 THEN "Mayo" WHEN MONTH(apc.fecha_crea) = 6 THEN "Junio"
WHEN MONTH(apc.fecha_crea) = 7 THEN "Julio" WHEN MONTH(apc.fecha_crea) = 8 THEN "Agosto"
WHEN MONTH(apc.fecha_crea) = 9 THEN "Septiembre" WHEN MONTH(apc.fecha_crea) = 10 THEN "Octubre"
WHEN MONTH(apc.fecha_crea) = 11 THEN "Noviembre" WHEN MONTH(apc.fecha_crea) = 12 THEN "Diciembre"
ELSE "Esto no es un Mes" END AS Mes1,apc.id_punto,
COUNT(1) AS Pedidos_cancelados,SUM(valor_orden) AS Venta_cancelada,cpdv.nombre as punto1
