CREATE DEFINER=`integration`@`%` PROCEDURE `sil_inicio_buscaorden`(_fechain VARCHAR(30),
_fechafin VARCHAR(30),
 _idorden VARCHAR(30), 
 _nombres VARCHAR(50),
  _telefono VARCHAR(30), 
_direccion VARCHAR(30),
 _codigosucursal VARCHAR(10),
  _grtafica INT,
 --   _tiempoest VARCHAR(8), 
_identificacion VARCHAR(50),
_idaliado VARCHAR(10))
BEGIN
    
IF  _idorden = '' THEN
        SET _idorden = '%%';
    ELSE
    -- set _idpedido = _idpedido;
    SET _idorden = CONCAT('%', _idorden,'%');
END IF;
IF  _nombres = '' THEN
        SET _nombres = '%%';
    ELSE
    SET _nombres = CONCAT('%', _nombres,'%');
END IF;
IF  _telefono = '' THEN
        SET _telefono = '%%';
    ELSE
    SET _telefono = CONCAT('%', _telefono,'%');
END IF;
IF  _direccion = '' THEN
        SET _direccion = '%%';
    ELSE
    SET _direccion = CONCAT('%', _direccion,'%');
END IF;
IF  _codigosucursal = '' THEN
        SET _codigosucursal = '%%';
    ELSE
    SET _codigosucursal = CONCAT('', _codigosucursal,'');
END IF;
IF  
_codigosucursal = '%0%' THEN
        SET _codigosucursal = '%%';
    ELSE
    SET _codigosucursal = CONCAT('', _codigosucursal,'');
END IF;
IF  _identificacion = '' THEN
        SET _identificacion = '%%';
    ELSE
    SET _identificacion = CONCAT('%', _identificacion,'%');
END IF;
IF  _idaliado = '' THEN
        SET _idaliado = '%%';
    ELSE
       SET _idaliado = CONCAT('%', _idaliado,'%');
END IF;
SELECT rog.nro_orden_general AS id,cpdv.nombre as restaurante,rog.nro_orden_dia AS consecutivo_web,
CAST(rog.fecha_crea AS CHAR) AS fecha,cu.id AS cod_cli,rog.nombre_cliente  AS nombre,
rog.direccion AS direc_ofi,rog.observaciones AS notas,rog.domiciliario AS motorizado,IF(rog.estado_orden != 1, 'Pedido Cancelado', 'Pedido Activo') AS status_pedido,
ceo.nombre AS estado_pedido,rog.valor_orden AS valor,rog.valor_propina AS propina,rog.empresa,CONVERT(MID(rog.fecha_crea,11,9), CHAR) AS hora_impresion,
rog.nro_orden_general as numero_orden
FROM cat_login_usuarios cu
INNER JOIN reg_orden_general rog ON cu.id = rog.id_cliente
INNER JOIN cat_punto_venta cpdv ON cpdv.id_punto = rog.id_punto
INNER JOIN cat_estado_orden ceo ON rog.estado_orden=ceo.estado_orden
WHERE (rog.nro_orden_general LIKE _idorden OR CONCAT(rog.id_punto, ' ', rog.factura) LIKE _idorden) AND 
rog.nro_orden_general LIKE _idorden
AND LEFT(rog.fecha_crea,10) BETWEEN _fechain AND _fechafin 
AND CONCAT(rog.nombre_cliente , ' ' , rog.empresa) LIKE _nombres
AND rog.identificacion LIKE _identificacion
AND CONCAT(rog.telefono_fijo, '', rog.telefono_celular) LIKE _telefono
AND rog.id_punto = _codigosucursal
GROUP BY rog.nro_orden_general;
    
    END ;;
