CREATE DEFINER=`integration`@`%` PROCEDURE `spcp_crm_buscapuntotraslado`(_idPunto INT)
BEGIN
declare _retorna INT;


select  hp.restaurante, concat(cast(hp.estado as char)," ",cast(ps.nombre as char)) as estado , 
hp.tiempoEntrega AS tiempo_entrega , ps.costoEnvio AS recargo_domicilio
from tcpc_puntoventa ps right join 
(
	 SELECT if(traslado > 0 ,puntoTraslado,  
	 if(curtime() < inicio,
	 idPunto,     
	 if(curtime() <= fin, idPunto,       
	 if(puntoTraslado > 0,puntoTraslado,          
	 idPunto)))) as restaurante,                           
	  if(traslado > 0 ,'Traslado obligado de pedido al punto : ',
	 if(curtime() < inicio, 'Pedido antes de horario  al punto : ', 
	 if(curtime() <= fin, 'Pedido en horario normal al punto : ', 
	 if(puntoTraslado > 0,'Traslado de pedido luego de horario al punto : ', 
	 'Pedido fuera de horario sin traslado al punto : '))))   as estado, 
	 tiempoEntrega 
	FROM tcpr_horariospuntoventa WHERE 
	 dayofweek(curdate()) = dia
	 and idPunto = _idPunto
) hp ON ps.idPunto = hp.restaurante
;
END ;;
