CREATE DEFINER=`integration`@`%` PROCEDURE `spcp_crm_calcularecargo`(_Valor INT, _idPunto INT)
BEGIN
DECLARE Valor_recargo INT;
declare _recargo_variable INT;
declare _recargo_domicilio INT;
SET Valor_recargo = _Valor;

SELECT  envioVariable INTO _recargo_variable  FROM tcpc_puntoventa WHERE idPunto=_idPunto AND activo=1;
SELECT  costoEnvio INTO  _recargo_domicilio FROM tcpc_puntoventa WHERE idPunto=_idPunto AND activo=1;

if(_recargo_variable=1) then
		IF( _Valor<=39000) THEN
			SET Valor_recargo = _recargo_domicilio;			
		END IF;
		
		IF(_Valor>39000 AND _Valor<=83000) THEN
			SET Valor_recargo = (_Valor*10/100);
			
		END IF;
		IF(_Valor>83000) THEN
			SET Valor_recargo = 8300;			
		END IF;
		
		ELSE
		SET Valor_recargo = _recargo_domicilio;
END IF;


SELECT Valor_recargo;


END ;;
