CREATE DEFINER=`integration`@`%` PROCEDURE `spcp_crm_citaspendientes`(_idUsuario INT, 
_fechaCita VARCHAR(20), 
_horaCita VARCHAR(10),
_idCampana int)
BEGIN
	declare _agendasasesor int;
	DECLARE _agendasasesorleidas INT;
	DECLARE _desicion varchar(10);
	
	
	SELECT COUNT(1) INTO _agendasasesor
		FROM tcpr_citas tc
		-- INNER JOIN agenda_tipo tipagenda ON tipagenda.codigo = a.tipo_cita
		-- INNER JOIN  maestro m ON a.codigo = m.codigo
		-- LEFT JOIN contacto c ON a.id_contacto  = c.id_contacto
		-- INNER JOIN status_vta_gestion svg  ON svg.maestro_codigo = m.codigo
		-- INNER JOIN  status_vta sv ON svg.statusvta_id = sv.status_vta
		-- left join cat_campanas camp on camp.codigo = a.campana
		WHERE tc.idUsuario  = _idUsuario 
		AND tc.fechaCita = _fechaCita
		AND tc.horaInicioCita <= _horaCita
		AND tc.confirmada  = 0
		AND tc.cancelaCita = 0 
		AND tc.asistio = 0 AND tc.idCampana = _idCampana;
		
	
		SELECT COUNT(1) INTO _agendasasesorleidas
		FROM tcpr_citas tc
		-- INNER JOIN agenda_tipo tipagenda ON tipagenda.codigo = a.tipo_cita
		-- INNER JOIN  maestro m ON a.codigo = m.codigo
		-- LEFT JOIN contacto c ON a.id_contacto  = c.id_contacto
		-- INNER JOIN status_vta_gestion svg  ON svg.maestro_codigo = m.codigo
		-- INNER JOIN  status_vta sv ON svg.statusvta_id = sv.status_vta
		-- left join cat_campanas camp on camp.codigo = a.campana
		WHERE tc.idUsuario  = _idUsuario 
		AND tc.fechaCita = _fechaCita
		AND tc.horaInicioCita <= _horaCita
		AND tc.confirmada=0
		AND tc.cancelaCita=0 
		AND tc.asistio=0 
		AND tc.leido=1 AND tc.idCampana = _idCampana;
	
	SELECT if(_agendasasesor = _agendasasesorleidas, 'SI', 'NO') INTO _desicion;
					
						IF _desicion = 'SI' THEN						
							UPDATE tcpr_citas tc
							SET tc.leido=0
							WHERE tc.idUsuario  = _idUsuario 
							AND tc.fechaCita = _fechaCita
							AND tc.horaInicioCita <= _horaCita
							AND tc.confirmada  = 0
							AND tc.cancelaCita = 0 
							AND tc.asistio = 0 AND tc.idCampana = _idCampana;
						END IF;			
						
		
		SELECT idCita,tc.idCliente,tc.idContacto,'Sin Dato' AS nombreContacto,'Sin Dato' AS empresaContacto,tcl.nombre AS empresa,tcl.nombre,tc.notas,CONVERT(DATE(tc.fechaCita), CHAR) AS fechaProg,tc.horaInicioCita
	FROM tcpr_citas tc
	INNER JOIN  tcpr_cliente tcl ON tcl.idCliente = tc.idCliente
	WHERE tc.idUsuario  = _idUsuario
	AND tc.fechaCita = _fechaCita
	AND tc.horaInicioCita <= _horaCita
	AND tc.confirmada  = 0
	AND tc.cancelaCita = 0 
	AND tc.asistio = 0 
	and tc.leido =0 and tc.idCampana = 1
	ORDER BY tc.horaInicioCita LIMIT 1;		
						


END ;;
