CREATE DEFINER=`integration`@`%` PROCEDURE `spcp_crm_creardireccioncliente`(_idDireccion int,
	_idCliente 			INT, 
	_via 	VARCHAR	(30), 
	_viaPrincipal VARCHAR(30),	 
	_viaSecundaria VARCHAR(50), 
	_viaComplemento VARCHAR(50),	
	_descripcionPredio VARCHAR(200), 
	_lat VARCHAR(100), 
	_lng VARCHAR(100), 
	_direccionActual INT, 
	_barrio VARCHAR(100), 
	_idDepartamento INT, 
	_idCiudad INT,
	_observacionesAdicionales	VARCHAR(500),
	_idPunto INT)
BEGIN

	UPDATE  tcpr_misdirecciones SET direccionActual = 0  WHERE idCliente = _idCliente;
    
    IF NOT EXISTS(SELECT idCliente FROM tcpr_misdirecciones  
    WHERE  idCliente = _idCliente  and idDireccion = _idDireccion)

        THEN
    
		    INSERT  tcpr_misdirecciones (idCliente, via, viaPrincipal, viaSecundaria, viaComplemento, descripcionPredio, lat, lng, direccionActual, barrio, idDepartamento, idCiudad,observacionesAdicionales,idPunto,fechaCrea,direccion)
		    VALUES (_idCliente, _via, _viaPrincipal, _viaSecundaria, _viaComplemento, _descripcionPredio, _lat, _lng, _direccionActual, _barrio, _idDepartamento, _idCiudad,_observacionesAdicionales,_idPunto,NOW(),CONCAT_WS(" ",_via,_viaPrincipal,'#',_viaSecundaria,'-',_viaComplemento,_descripcionPredio,_observacionesAdicionales));
	    ELSE
		     UPDATE tcpr_misdirecciones 
		     SET via=_via, 
		     viaPrincipal=_viaPrincipal, 
		     viaSecundaria=_viaSecundaria, 
		     viaComplemento=_viaComplemento,
		     observacionesAdicionales = _observacionesAdicionales,
		     direccionActual= _direccionActual,
		     barrio = _barrio,
		     descripcionPredio = _descripcionPredio,
				 fechaModi = NOW(),
				 direccion = CONCAT_WS(" ",_via,_viaPrincipal,'#',_viaSecundaria,'-',_viaComplemento,_descripcionPredio,_observacionesAdicionales)
		     WHERE  idCliente = _idCliente  AND idDireccion = _idDireccion;

	    
   END IF;
END ;;
