CREATE DEFINER=`integration`@`%` PROCEDURE `spcp_crm_estadoasesor`(_idUsuario INT,
_idEstado  INT,
_nombreEstado   VARCHAR(20),
_idCampana INT)
BEGIN
DECLARE _fechaFin VARCHAR (20);

#LCQ= 03-04-2020 . Se agrega para saber el estado y fecha y hora cuando ingresa el TME.
	IF _idEstado = 1 THEN
		UPDATE tcpc_loginusuarios SET login=1,fechaUltimoIngreso= NOW() where idUsuario=_idUsuario;
		END IF;

#LCQ= 03-04-2020 . Se agrega para saber el estado y fecha y hora cuando ingresa el TME.
IF _idEstado = 9 THEN
	UPDATE tcpc_loginusuarios SET login=0,fechaSalida= NOW() WHERE idUsuario=_idUsuario;
	END IF;

IF _idEstado = 9 THEN
	SET _fechaFin = NOW();
	ELSE
	SET _fechaFin = '1981-01-07';
	END IF;


UPDATE `tcpr_estadoasesor`
SET fechaFin = NOW() WHERE idUsuario = _idUsuario 
AND LEFT(fechaFin, 10) = '1981-01-07';


INSERT INTO `tcpr_estadoasesor`
	(`idUsuario`,
	`idEstado`,
	`fechaInicio`,
	`fechaFin`,
	`nombreEstado`,
	`idCampana`)
	VALUES
	(
	_idUsuario,
	_idEstado,
	NOW(),
	_fechaFin,
	_nombreEstado,
	_idCampana);
	COMMIT;


#LCQ= 03-04-2020. Se quita el filtro de la campaña para que no duplica el usuario en el acw
IF EXISTS(SELECT idEstadoActual FROM tcpr_estadoasesoractual 
		  WHERE idUsuario = _idUsuario)  --  AND codigo_campana = _idCampana) LCQ
			THEN
				UPDATE  tcpr_estadoasesoractual 
				SET idEstado = _idEstado,
					fechaInicio = NOW(),
					nombreEstado = _nombreEstado,
					idCampana = _idCampana
					WHERE idUsuario = _idUsuario;
					-- AND codigo_campana = _idCampana;
			ELSE
			INSERT INTO tcpr_estadoasesoractual 
						(nombreEstado, idCampana, idUsuario, idEstado, fechaInicio)
			VALUES(_nombreEstado,_idCampana,_idUsuario,_idEstado ,NOW());
			END IF;
			SELECT 'OK' AS resultado;


END ;;
