CREATE DEFINER=`integration`@`%` PROCEDURE `spcp_crm_georeferenciacionpunto`(_h1 DOUBLE, 
  _v1 DOUBLE,
  _idCiudad INT,
  _idMarca INT)
BEGIN
  IF EXISTS (SELECT idPoligono FROM tcpr_poligonos WHERE x1 > x2 ) 
    THEN
        DROP TABLE IF EXISTS `tcpr_poligonos_tempo`; 
        CREATE TEMPORARY TABLE `tcpr_poligonos_tempo` AS SELECT * FROM tcpr_poligonos WHERE x1 > x2; 
        UPDATE tcpr_poligonos_tempo dpt , tcpr_poligonos dp SET dp.x1 = dpt.x2, dp.y1 = dpt.y2, dp.x2 = dpt.x1, dp.y2 = dpt.y1 WHERE dp.idPoligono = dpt.idRectangulo; 
		TRUNCATE TABLE tcpr_rectangulos;
		INSERT INTO tcpr_rectangulos (idPunto, x1, y1, x2, y2)
		SELECT idPunto, MIN(x1),MIN(y1),MAX(x2),MAX(y2) FROM tcpr_poligonos GROUP BY idPunto;
	END IF;
     
    
SELECT r.idPunto AS zona ,
 SUM(IF(_h1  >= (_v1 * p.x2 - _v1 * p.x1 - p.x2*p.y1 + p.x1*p.y2)/(p.y2 - p.y1),IF((_v1 * p.x2 - _v1 * p.x1 - p.x2*p.y1 + p.x1*p.y2)/(p.y2 - p.y1) >=p.x1, 
IF( (_v1 * p.x2 - _v1 * p.x1 - p.x2*p.y1 + p.x1*p.y2)/(p.y2 - p.y1) <=p.x2  ,1,0),0)  ,0))  MOD 2 AS dentro 
FROM tcpr_rectangulos r, tcpr_poligonos p 
WHERE p.idPunto = r.idPunto 
AND  _h1 >= r.x1 AND _h1 <= r.x2 AND _v1 >= r.y1 AND _v1 <= r.y2
AND idCiudad = _idCiudad
AND idMarca = _idMarca
GROUP BY p.idPunto;
END ;;
