BEGIN

	declare cadena text default '';
	
	if _idContacto > 0 then
		SET cadena = "SELECT tct.nombre AS tipo_contacto,tcl.fechaLlamada AS fecha, tcl.horaLlamada AS hora_inicio,
	tcc.confirmada,tcc.cancelaCita AS cancela_cita,tcc.asistio,'Ejecutivo 1'responsable,tcc.notas,tcc.fechaCrea AS fecha_creacion, tcc.ejecutivo ,tcc.idCita as id
