		ELSE
			SET cadena = CONCAT(cadena, ' ' , _idCliente, ' and tcc.idContacto = ', _idContacto);
		END IF;
		SET cadena = CONCAT(cadena, " GROUP BY tcc.idCita ORDER BY tcc.fechaCrea DESC") ;
		
		
		
		
	else
		SET cadena = "	SELECT tct.nombre AS tipo_contacto,tcl.idContacto AS contacto,tr.nombre AS resultado_gestion, ttp.nombre AS estado_gestion,tcl.fechaLlamada AS fecha, tcl.horaLlamada AS hora_inicio,
