	INNER JOIN tcpr_llamadas tcl ON tcc.idCliente=tcl.idCliente
	INNER JOIN tcpc_tipificaciones ttp ON ttp.idTipificacion = tcl.idTipificacion
	INNER JOIN tcpc_citastipo tct ON tct.idTipoCita=tcc.idTipoCita
	INNER JOIN tcpc_resultados tr ON tr.idResultado = tcl.idResultado
	WHERE tcc.idTipoCita=2 AND tcl.idCliente=";
		
