CREATE DEFINER=`integration`@`%` PROCEDURE `spcp_crm_historialordendetalle`(_idOrdenGeneral INT)
BEGIN
DECLARE valorinicio INT;


SELECT COUNT(1) INTO valorinicio FROM tcpr_ordendetalle WHERE idOrdenGeneral  = _idOrdenGeneral;


IF  valorinicio <> 0 THEN


	SELECT  a.idOrdenGeneral AS Pedido,  apc.idOrdenDia AS Pedido_dia,m.idCliente AS codigo,m.nombre  AS nombre ,
	m.direccion AS direccion, m.telefonoFijo AS telefono, apc.observaciones as notas,
	a.skuItem AS cod_producto, a.idPunto AS sector, s.nombre AS nombre_punto,
	IF(MID(a.observacion,1,9)='seleccion', CONCAT('  * ',  a.itemNombre),a.itemNombre)  AS producto,
	IF(MID(a.observacion,1,9)='seleccion','Opcion',CONVERT(a.cantidad,CHAR)) AS cantidad, a.valorBaseUni AS precio,a.valorUnitario AS precio_negociado, a.idPunto AS cod_sector, s.nombre AS sector 
	,CONVERT(NOW(), CHAR) AS fecha ,apt.nombre  AS FormaPago,us.nombre AS usuario	,CONVERT(a.fechaCrea, CHAR) fecha_pedido,apc.idOrdenDia AS Consecutivo, 
	IF(aps.nombre = 'VENTA', 'PEDIDO',aps.nombre) AS cabecera, a.observacion AS notasproforma 
	,pc.nombre AS ciudad,m.barrio AS Barrio,'1981-01-07' AS fecha_nace,'MASCULINO' AS sexo,	" "  AS profesion ,'0' AS hijos, m.email, m.extension AS telf_ofi_ext, m.telefonoCelular AS celular
	, m.identificacion, mr.nombre AS marca, IF(apf.valor IS NULL, apc.totalOrden, apf.valor) AS valorpedido,
	CONCAT('DM', '-', apc.idPunto, ' ', apc.factura) AS factura_real, apc.factura AS factura_numero, s.nombre AS ciudad, s.idMarca AS id_area, s.idCiudad AS cod_ciudad,
	apc.direccion AS direccion_pedido, apc.idOrdenNumero AS codigocom, mr.idMarca AS codigomarca, apc.idOrdenNumero AS numero_orden
	FROM tcpr_cliente m 
	INNER JOIN tcpr_ordengeneral apc ON m.idCliente = apc.idCliente
	INNER JOIN tcpr_ordendetalle a  ON apc.idOrdenGeneral=a.idOrdenGeneral
	-- INNER JOIN a_pro_pago ap ON ap.factura = a.idOrdenGeneral 
	LEFT JOIN tcpr_ordentipopago apf ON apc.idOrdenGeneral = apf.idOrdenGeneral
	LEFT JOIN tcpc_tipopago apt ON apt.idTipoPago=apf.idTipoPago
	INNER JOIN tcpc_puntoventa s ON s.idPunto = a.idPunto
	INNER JOIN tcpc_loginusuarios us ON us.idUsuario = apc.idUsuario
	INNER JOIN tcpc_estadoorden aps ON apc.idEstadoOrden = aps.idEstadoOrden
	INNER JOIN tcpc_ciudad pc ON m.idCiudad = pc.idCiudad
	INNER JOIN tcpc_marca mr ON apc.idMarca = mr.idMarca
	WHERE a.idOrdenGeneral  = _idOrdenGeneral 
	GROUP BY a.id
	ORDER BY a.id;

END IF;


IF  valorinicio = 0 THEN

-- LCQ = 06-06-2023  Se debe colocar informacion del la base historica 

	SELECT  a.idOrdenGeneral AS Pedido,  apc.idOrdenDia AS Pedido_dia,m.idCliente AS codigo,m.nombre  AS nombre ,
	m.direccion AS direccion, m.telefonoFijo AS telefono, apc.observaciones as notas,
	a.skuItem AS cod_producto, a.idPunto AS sector, s.nombre AS nombre_punto,
	IF(MID(a.observacion,1,9)='seleccion', CONCAT('  * ',  a.itemNombre),a.itemNombre)  AS producto,
	IF(MID(a.observacion,1,9)='seleccion','Opcion',CONVERT(a.cantidad,CHAR)) AS cantidad, a.valorBaseUni AS precio,a.valorUnitario AS precio_negociado, a.idPunto AS cod_sector, s.nombre AS sector 
	,CONVERT(NOW(), CHAR) AS fecha ,apt.nombre  AS FormaPago,us.nombre AS usuario	,CONVERT(a.fechaCrea, CHAR) fecha_pedido,apc.idOrdenDia AS Consecutivo, 
	IF(aps.nombre = 'VENTA', 'PEDIDO',aps.nombre) AS cabecera, a.observacion AS notasproforma 
	,pc.nombre AS ciudad,m.barrio AS Barrio,'1981-01-07' AS fecha_nace,'MASCULINO' AS sexo,	" "  AS profesion ,'0' AS hijos, m.email, m.extension AS telf_ofi_ext, m.telefonoCelular AS celular
	, m.identificacion, mr.nombre AS marca, IF(apf.valor IS NULL, apc.totalOrden, apf.valor) AS valorpedido,
	CONCAT('DM', '-', apc.idPunto, ' ', apc.factura) AS factura_real, apc.factura AS factura_numero, s.nombre AS ciudad, s.idMarca AS id_area, s.idCiudad AS cod_ciudad,
	apc.direccion AS direccion_pedido, apc.idOrdenNumero AS codigocom, mr.idMarca AS codigomarca, apc.idOrdenNumero AS numero_orden
	FROM tcpr_cliente m 
	INNER JOIN tcpr_ordengeneral apc ON m.idCliente = apc.idCliente
	INNER JOIN tcpr_ordendetalle a  ON apc.idOrdenGeneral=a.idOrdenGeneral
	-- INNER JOIN a_pro_pago ap ON ap.factura = a.idOrdenGeneral 
	LEFT JOIN tcpr_ordentipopago apf ON apc.idOrdenGeneral = apf.idOrdenGeneral
	LEFT JOIN tcpc_tipopago apt ON apt.idTipoPago=apf.idTipoPago
	INNER JOIN tcpc_puntoventa s ON s.idPunto = a.idPunto
	INNER JOIN tcpc_loginusuarios us ON us.idUsuario = apc.idUsuario
	INNER JOIN tcpc_estadoorden aps ON apc.idEstadoOrden = aps.idEstadoOrden
	INNER JOIN tcpc_ciudad pc ON m.idCiudad = pc.idCiudad
	INNER JOIN tcpc_marca mr ON apc.idMarca = mr.idMarca
	WHERE a.idOrdenGeneral  = _idOrdenGeneral 
	GROUP BY a.id
	ORDER BY a.id;
END IF;
END ;;
