CREATE DEFINER=`integration`@`%` PROCEDURE `spcp_crm_historialordenseguimiento`(_idOrdenGeneral INT)
BEGIN

DECLARE _estado VARCHAR(40);
SET _estado = "Estado_Pedido :  E N   G E S T I O N";
if exists(select idTraza from tcpr_trazas where idTraza = 6 and idIncidente = 5 and  idOrdenGeneral = _idOrdenGeneral) then
	set _estado  = "Estado_Pedido :   E N T R E G A D O";
end if;
IF EXISTS(SELECT idTraza FROM tcpr_trazas WHERE  idTraza = 7   AND  idOrdenGeneral = _idOrdenGeneral) THEN
	SET _estado  = "Estado_Pedido :  C A N C E L A D O";
END IF;


SELECT  t.nombre AS Trazas, nov.nombre as Novedades, idOrdenGeneral AS pedido, CONVERT(fechaInicio, CHAR) AS fecha_inicio,  
CONVERT(TIMEDIFF(IF(fechaFin='1981-01-07 00:00:00', NOW(), fechaFin), fechaInicio), CHAR) AS tiempo, observaciones AS nota,
 concat(u.nombre, ' (', u.celular, ')') as Domiciliario, _estado AS estado, 
 idOrdenGeneral AS dto_nro
FROM tcpr_trazas g 
INNER JOIN tcpc_traza t ON g.idTraza = t.idTraza 
left join tcpc_incidentemotorizado nov on nov.idIncidente = g.idIncidente
left join tcpc_loginusuarios u on g.idDomiciliario = u.idUsuario
WHERE g.idOrdenGeneral = _idOrdenGeneral
ORDER BY g.idTraza DESC ;
 

END ;;
