CREATE DEFINER=`integration`@`%` PROCEDURE `spcp_crm_historialordentraslada`(_idOrdenGeneral INT,
_idPunto INT,
_notas  VARCHAR(200),
_idUsuario INT)
BEGIN
DECLARE _setor_cambio INT;
DECLARE _sector_default INT;
DECLARE _retorna INT;
DECLARE _destinoidOrdenDia INT;
-- DECLARE _ofi_sector INT;
DECLARE _sector_origen INT;
-- SET _ofi_sector = _idPunto;


				#LCQ = 10-03-2023 Genera el consecutivo diario de la orden
	-- ---------------------------------------------------------------------------------------------------------------------
		IF EXISTS(SELECT idOrdenDia FROM tcpc_puntoventa WHERE DATE(fechaOrdenDia)=CURDATE() AND idPunto= _idPunto) THEN
				SELECT (idOrdenDia + 1) INTO _destinoidOrdenDia FROM tcpc_puntoventa WHERE idPunto= _idPunto FOR UPDATE;
				UPDATE tcpc_puntoventa SET idOrdenDia = LAST_INSERT_ID(idOrdenDia + 1),fechaOrdenDia=NOW()  WHERE idPunto = _idPunto;
				COMMIT;		
				ELSE		
				UPDATE tcpc_puntoventa SET fechaOrdenDia = NOW(), idOrdenDia = 0 WHERE idPunto = _idPunto;
				SELECT (idOrdenDia + 1) INTO _destinoidOrdenDia FROM tcpc_puntoventa WHERE idPunto= _idPunto FOR UPDATE;
				UPDATE tcpc_puntoventa SET idOrdenDia = LAST_INSERT_ID(idOrdenDia + 1),fechaOrdenDia=NOW()  WHERE idPunto = _idPunto;
				COMMIT;
				END IF;


	UPDATE tcpr_trazas 
		SET  idPuntoOrigen = idPunto,
			fechaTraslado=NOW(),
			idUsuarioTraslado=_idUsuario,
			observaciones = left(concat(observaciones," Traslado desde ", _idPunto,  " Usuario ", _idUsuario, " Fecha: ", now(), " Motivo : ", _notas),100),
			idPunto = _idPunto,
 			idUsuario = _idUsuario,	
 			fechaFin = '1981-01-07 00:00:00' 					
			WHERE  idOrdenGeneral = _idOrdenGeneral AND idTraza = 1;
			COMMIT;	 
			DELETE FROM  tcpr_trazas WHERE  idOrdenGeneral = _idOrdenGeneral AND idTraza != 1;
			COMMIT;
				
	 
	UPDATE tcpr_trazasdia 
		SET  idPuntoOrigen = idPunto,
			fechaTraslado=NOW(),
			idUsuarioTraslado=_idUsuario,
			observaciones = left(concat(observaciones," Traslado desde ", _idPunto,  " Usuario ", _idUsuario, " Fecha: ", now(), " Motivo : ", _notas),100),
			idPunto = _idPunto,
 			idUsuario = _idUsuario,	
 			fechaFin = '1981-01-07 00:00:00' 					
			WHERE  idOrdenGeneral = _idOrdenGeneral AND idTraza = 1;
			COMMIT;	 
			DELETE FROM  tcpr_trazasdia WHERE  idOrdenGeneral = _idOrdenGeneral AND idTraza != 1;
			COMMIT;
 	 
	UPDATE  tcpr_ordengeneral 
		SET observaciones = concat(' Orden Trasladada Desde: ', idPunto,' ',observaciones),idPunto = _idPunto,idMultiPunto = _idPunto,	idOrdenDia = _destinoidOrdenDia,fechaEstado=NOW(),
		impresion= 0
		WHERE idOrdenGeneral = _idOrdenGeneral;
		COMMIT;
 
  
	UPDATE tcpr_ordendetalle 
		SET idPunto = _idPunto,idMultiPunto = _idPunto
		WHERE idOrdenGeneral = _idOrdenGeneral;
    SET _retorna = row_count();
		COMMIT;
 

 
 UPDATE tcpr_ordentipopago
		SET  idPunto = _idPunto
		WHERE idOrdenGeneral = _idOrdenGeneral;
		COMMIT;
     

SELECT  _retorna;

END ;;
