CREATE DEFINER=`integration`@`%` PROCEDURE `spcp_crm_indicativollamada`(_idCliente INT, _telefono VARCHAR(20), _idContacto INT)
BEGIN
	DECLARE _idPais VARCHAR(10) DEFAULT "";  
	DECLARE _idCiudad VARCHAR(10) DEFAULT "";  
	DECLARE _caracteres INT DEFAULT 0;  
	DECLARE _tiporegistro VARCHAR(10) DEFAULT "";  
	DECLARE _configuracionpais VARCHAR(20) DEFAULT "";  
	DECLARE _configuracionciudad VARCHAR(20) DEFAULT "";  
	
	-- if _idContacto > 0 then
		SELECT idPais INTO _idPais FROM tcpr_cliente WHERE idCliente = _idCliente;  
		SELECT idCiudad INTO _idCiudad FROM tcpr_cliente WHERE idCliente = _idCliente;  
	-- end if;
	
	SELECT LENGTH(_telefono) INTO _caracteres;  
	
	SELECT IF(digitosFijo >= _caracteres, 'FIJO', 'CELULAR') INTO _tiporegistro FROM tcpc_pais WHERE idPais = _idPais; 
	IF _tiporegistro = 'FIJO' THEN   
		if _idPais = '7' THEN
			if _idCiudad = '11001' then
				select concat('9',_telefono) as telefono; 
			ELSE 
				SELECT indicativo INTO _configuracionciudad FROM tcpc_ciudad WHERE idCiudad = _idCiudad; 
				select concat('905',_configuracionciudad, _telefono) as telefono; 
			end if;
			
			
		else
		SELECT CONCAT(prefijoSalida, prefijoOperadorInternacional, prefijoPais,prefijoCelular) INTO _configuracionpais 
		FROM tcpc_pais WHERE idPais = _idPais;  
		 
		SELECT indicativo INTO _configuracionciudad FROM tcpc_ciudad WHERE idCiudad = _idCiudad;   
		SELECT CONCAT(_configuracionpais, _configuracionciudad, _telefono) AS telefono;  
		end if;
		
	ELSE   
		SELECT CONCAT(prefijoSalida, prefijoOperadorInternacional, prefijoPais,prefijoCelular) INTO _configuracionpais 
		FROM tcpc_pais WHERE idPais = _idPais;   
		SELECT CONCAT(_configuracionpais, _telefono) AS telefono;  
	END IF;

END ;;
