CREATE DEFINER=`integration`@`%` PROCEDURE `spcp_crm_itemseleccion`(_skuItem varchar(20),
_idPunto INT,
_idOrdenGeneral INT)
BEGIN

DECLARE _listaPrecio INT;
SET _listaPrecio = 0;

IF EXISTS(SELECT listaPrecio  from tcpc_puntoventa where idPunto = _idPunto) THEN
				SELECT listaPrecio INTO _listaPrecio FROM tcpc_puntoventa WHERE idPunto = _idPunto;
					END IF;


		IF NOT EXISTS(SELECT skuItem FROM tcpr_itemsrelgruposeleccion  WHERE skuItem = _skuItem) THEN


			IF _idOrdenGeneral = 0 THEN

					SELECT IF(gru.nombre IS NULL, prod.idFamilia,'FAMILIA CATEGORIA') AS familia_categoria,prod.itemNombre as producto,IF(gru.nombre IS NULL,opc.idFamilia,'FAMILIA GRUPO') AS familia_grupo,
					gru.nombre as grupo, gru.idItemGrupo AS CODIGO_GRUPO,
					opc.itemNombre as combinado,pp.precio AS precio_caja,
					relopc.tipoOpcion AS tipo_opcion ,
					relopc.porDefecto AS por_defecto, 
					relgru.cantidadMaxima AS cantidad_maxima,prod.skuItem AS cod_prod,prod.idHomologacion AS cod_prod_hom, gru.idItemGrupo AS grupo_opcion,opc.skuItem AS cod_opc,opc.idHomologacion AS cod_opc_hom, prod.descripcion,pp.unidad-- ,IF(pre.precio IS NULL, 0,pre.precio) AS precio_caja_seleccion		
					FROM tcpc_items prod
					INNER  JOIN tcpr_itemslistaprecio pp ON prod.skuItem = pp.skuItem				
					LEFT JOIN tcpr_itemsrelgruposeleccion relgru on prod.skuItem = relgru.skuItem				
					LEFT JOIN tcpc_itemgruposeleccion gru on relgru.idItemGrupo = gru.idItemGrupo				
					LEFT JOIN tcpr_itemseleccion relopc on relopc.idItemGrupo = relgru.idItemGrupo				
					LEFT JOIN tcpc_items opc on opc.skuItem = relopc.skuSeleccion
					LEFT JOIN tcpr_itemslistaprecio pre on opc.skuItem = pre.skuItem
					WHERE prod.skuItem = _skuItem 
					AND pp.listaPrecio = _listaPrecio 
					AND (relopc.activo =1 or relopc.activo is null);
				
				ELSE
				
