select tpg.fechaCrea AS FechaCaso,tpi.idPqr as Caso, tpe.nombre as Responsable, tpt.nombre as Tipo, tptt.nombre as Tema, tps.nombre as Subtema,  tpi.descripcionIngreso as RequerimientoCliente,  
