CREATE DEFINER=`integration`@`%` PROCEDURE `spcp_impresion_detalle_comanda`(_codRestaurante INT, 
_estado INT, _idOrdenGeneral INT)
BEGIN
DECLARE _NameOperador VARCHAR(3);
DECLARE  pedidoOnline VARCHAR(20);

#LCQ = 24-01-2023 Actualiza los item SEL para la impresion
IF _idOrdenGeneral > 0 THEN
UPDATE tcpr_ordendetalle rod SET observacion = 'seleccion'
WHERE valorUnitario=0 AND idOrdenGeneral=  _idOrdenGeneral;
END IF;

#LCQ = 24-01-2023 Trae el número de pedido online
SELECT idOrdenGeneral INTO  pedidoOnline FROM tcpr_ordengeneral  WHERE idOrdenGeneral = _idOrdenGeneral;

#LCQ = 24-01-2023 Actualiza campos de la direccion con espacios
IF _idOrdenGeneral > 0 THEN
		UPDATE  tcpr_ordengeneral
		SET direccion = REPLACE( direccion, "    ", " ")
		WHERE  idOrdenGeneral = _idOrdenGeneral;
		
		UPDATE  tcpr_ordengeneral
		SET direccion = REPLACE( direccion, "   ", " ")
		WHERE  idOrdenGeneral = _idOrdenGeneral;
		UPDATE  tcpr_ordengeneral
		SET direccion = REPLACE( direccion, "  ", " ")
		WHERE  idOrdenGeneral = _idOrdenGeneral;
		
	END IF;
	
	
	#LCQ = 24-01-2023 Valida si existe pedidos sin la impresion
	 
		IF _idOrdenGeneral = 0 THEN
			
			SELECT rog.idOrdenGeneral AS pedido
			FROM tcpr_ordengeneral rog
			WHERE  rog.idPunto = _codRestaurante
			AND rog.impresion=0
			GROUP BY rog.idOrdenGeneral;
		
		ELSE
		
		
 	SELECT rog.idOrdenDia AS pedido_conce_dia, rog.idOrdenGeneral AS pedido, '' AS contrato,
	rog.idCliente as codigo, rog.nombreCliente AS nombre,
        SUBSTRING(rog.direccion,1,24) AS dir1,
        IF(LENGTH(rog.direccion) > 24, SUBSTR(rog.direccion,25,30),'') AS dir2,
	IF(LENGTH(rog.direccion) > 55, SUBSTR(rog.direccion,55,30),'') AS dir3,
	rog.direccion AS direccion, CONCAT(rog.telefonoFijo, '-', rog.telefonoCelular) AS telefono, rod.observacion as notas,
	SUBSTR(rod.observacion,1,30) AS notas1_30,
	IF(LENGTH(rod.observacion) > 30, SUBSTR(REPLACE(rod.observacion, "\n",""),31,30),'') AS notas31_60,
	IF(LENGTH(rod.observacion) > 60, SUBSTR(REPLACE(rod.observacion, "\n",""),61,30),'') AS notas61_90,
	IF(LENGTH(rod.observacion) > 90, SUBSTR(REPLACE(rod.observacion, "\n",""),91,30),'') AS notas91_120,
	IF(LENGTH(rod.observacion) > 120, SUBSTR(REPLACE(rod.observacion, "\n",""),121,30),'') AS notas121_150,
	rod.skuItem AS cod_producto,
	IF(rod.valorUnitario=0, CONCAT('  * ',  REPLACE(rod.itemNombre,"-1","")),
	CONCAT('  ',rod.skuItem, ' ' , rod.itemNombre, '  $ ',CAST(rod.valorUnitario AS DECIMAL(20))) )  AS producto,
	CONCAT(rod.cantidad, '      ', rod.itemNombre)   AS producto1,
	MID(CONCAT(rod.skuItem, ' ' , rod.itemNombre , ' $ ', CAST(rod.valorUnitario AS DECIMAL(20)) ),30,60) AS producto2,
        IF(LENGTH(rod.observacion)>2,CONCAT("[", rod.observacion, "]",""),"") AS notaprod,
	IF(rod.valorUnitario=0 AND TRIM(REPLACE(rod.observacion,"\n"," ")) = 'seleccion','',CONCAT(CONVERT(rod.cantidad, CHAR), "  ")) AS cantidad,
        rod.valorUnitario AS precio, rog.idPunto AS cod_sector,cpdv.nombre  AS sector,  CONVERT(NOW(), CHAR) AS fecha,
	'Web' AS usuario,CONVERT(LEFT(rog.fechaCrea, 10), CHAR) fecha_pedido,
	rog.idOrdenDia AS Consecutivo,'ORDEN' AS cabecera, rod.observacion AS notasproforma,
	gc.nombre AS ciudad,rog.barrio,"OrdenOnline" AS origen,'' AS referencia,'' AS referencia2,
	rog.empresa,rog.identificacion AS ced_ident,
	'Pendiente' AS telf_ofi_ext, 'Pendiente' AS bono1, 'Pendiente' AS bono2 ,
        'ACTIVO' AS esactivo, cpdv.cajaNumero AS caja_numero, rog.idOrdenGeneral AS pedidoOnline,"AFILIADO" AS esafiliado,rog.valorPropina as propina,rod.valorTotal AS precio
    FROM -- cat_login_usuarios cu 	INNER JOIN 
	tcpr_ordengeneral rog -- ON cu.id = rog.id_cliente
	INNER JOIN tcpr_ordendetalle rod ON rod.idOrdenGeneral = rog.idOrdenGeneral  AND rog.idPunto=rod.idPunto
	INNER JOIN tcpc_puntoventa cpdv ON cpdv.idPunto = rog.idPunto
	INNER JOIN tcpc_ciudad gc ON rog.idCiudad= gc.idCiudad
	WHERE rog.idOrdenGeneral = _idOrdenGeneral
	ORDER BY rod.id;
END IF;

END ;;
