CREATE DEFINER=`integration`@`%` PROCEDURE `spcp_impresion_detalle_factura`(_codRestaurante INT, 
_estado INT, 
_idOrdenGeneral INT)
BEGIN
DECLARE _NameOperador VARCHAR(3);
DECLARE Resolucion VARCHAR(300);
DECLARE FACTURA_AUTO INT; 
 

#LCQ = 24-01-2023 Permite traer la resolucion de factura del cliente
SELECT CONCAT('Resolucion DIAN   ', resolucionDian, ' \n  ',
'Fecha Resolución:  ',  left(fechaResolucion,10),
'\n',
 'Fact. Autorizadas:  D', idPunto, '-', facturaInicial, '  \n  ', 'D', idPunto,'-', facturaFinal, " \n ")
 INTO Resolucion
FROM tcpc_puntoventa  WHERE idPunto = _codRestaurante;

#LCQ = 24-01-2023 Permite traer el tipo de impresion asignado
SELECT imprimeFacturaAuto INTO FACTURA_AUTO FROM tcpc_puntoventa WHERE idPunto = _codRestaurante LIMIT 1;


#LCQ = 24-01-2023 Actualiza el tipo de impresion
IF _idOrdenGeneral > 0 THEN
	UPDATE  tcpr_ordengeneral rog
	SET rog.impresion = 2
	WHERE rog.impresion = 1;	
			
	END IF;
	
			-- Lcq= 2020-04-30 Filtra pedidos pendientes por imprimir
		
		IF _idOrdenGeneral = 0 THEN
			SELECT rog.idOrdenGeneral AS pedido, rog.impresion , FACTURA_AUTO
			FROM tcpr_ordengeneral rog
			WHERE  rog.idPunto = _codRestaurante
			AND rog.impresion IN(0)
			GROUP BY rog.idOrdenGeneral;
		
		ELSE
		
	-- Lcq= 2020-04-30 genera informacion necesaria para la impresión de la factura. 
	
 			SELECT rog.idPunto AS punto, cpdv.prefijoFactura AS factura_prefijo, rog.factura as factura_numero,
 			rog.idOrdenDia AS pedido_conce_dia, rog.idOrdenGeneral AS pedido, '' AS contrato,
			rog.idCliente as codigo,
			IF(LENGTH(rog.empresa)>3,rog.empresa,rog.nombreCliente)  AS nombre ,
			rog.nombreCliente  AS contacto,
			SUBSTRING(rog.direccion,1,31) AS dir1,
			-- SUBSTRING(apc.direccion_pedido,1,31) AS dir1,
			IF(LENGTH(rog.direccion) > 31, SUBSTR(rog.direccion,31,36),'') AS dir2,
			IF(LENGTH(rog.direccion) > 58, SUBSTR(rog.direccion,67,36),'') AS dir3,
			IF(LENGTH(rog.direccion) > 75	, SUBSTR(rog.direccion,103,36),'') AS dir4,
			rog.direccion AS direccion,
			CONCAT(rog.telefonoFijo, '-', rog.telefonoCelular) AS telefono
			, rod.observacion,
			SUBSTR(rod.observacion,1,30) AS notas1_30
			, IF(LENGTH(rod.observacion) > 30, SUBSTR(rod.observacion,31,30),'') AS notas31_60
			, IF(LENGTH(rod.observacion) > 60, SUBSTR(rod.observacion,61,30),'') AS notas61_90
			, IF(LENGTH(rod.observacion) > 90, SUBSTR(rod.observacion,91,30),'') AS notas91_120
			, IF(LENGTH(rod.observacion) > 120, SUBSTR(rod.observacion,121,30),'') AS notas121_150,
			rod.skuItem AS cod_producto, rod.tipoItem as producto_tipo,
			CONCAT(rod.itemNombre, '   ', rod.cantidad, '  $ ',CAST((rod.valorUnitario * rod.cantidad ) AS DECIMAL(20)))   AS producto,
			-- CONCAT(rod.cantidad, '              ', rod.itemNombre, '                 $ ',CAST((rod.valorUnitario * rod.cantidad ) AS DECIMAL(20)))   AS producto1,
			CONCAT(rod.cantidad, '        ', rod.itemNombre,'                 $ ',CAST((rod.valorUnitario * rod.cantidad ) AS DECIMAL(20)))   AS producto1,
		        -- CONCAT(rod.cantidad, '  ', MID(rod.itemNombre,1,30))  AS producto1,
			MID(CONCAT(rod.itemNombre , ' $ ', CAST(rod.valorUnitario AS DECIMAL(20)) ),80,60) AS producto2,
			IF(LENGTH(rod.observacion)>2,CONCAT("              ",  SUBSTRING(REPLACE(rod.observacion, "\n"," "),1,39), "",""),"") AS notaprod,
			IF(rod.valorUnitario=0 AND TRIM(REPLACE(rod.observacion,"\n"," ")) = 'seleccion','',CONCAT(CONVERT(rod.cantidad, CHAR), "  ")) AS cantidad,
			IF(rod.tipoItem='S',      CONCAT( "        ", rod.cantidad , '  ',rod.itemNombre),'') AS producto_seleccion,
			IF(rod.tipoItem='S', '',  CONCAT("   ",rod.cantidad , ' X ', CAST(rod.valorUnitario  AS DECIMAL(20))))    AS precio_producto_p,
			rod.valorUnitario, rog.idPunto AS cod_sector,  cpdv.nombre  AS sector ,  CONVERT(NOW(), CHAR) AS fecha,
			'Web' AS usuario,CONVERT(LEFT(rog.fechaCrea, 10), CHAR) AS  fecha_pedido,CONVERT(MID(rog.fechaCrea,11,9), CHAR) AS hora_pedido
			  ,rog.idOrdenDia AS Consecutivo,   'Pendiente'  AS cabecera, rod.observacion AS notasproforma
				,gc.nombre AS ciudad,rog.barrio as ofi_barrio,'' AS referencia,'' AS referencia2,
			       --  IF(TRIM(ap.referencia) = 'ND', '',MID(ap.referencia,1,30)) AS referencia,
				     -- IF(TRIM(ap.referencia) = 'ND', '',MID(ap.referencia,30,68)) AS referencia2,
				rog.empresa,rog.identificacion AS ced_ident,
				'Pendiente' AS telf_ofi_ext, 'Pendiente' AS bono1, 'Pendiente' AS bono2 ,'Pendiente' AS esactivo, cpdv.cajaNumero AS caja_numero,
				 CAST(rog.valorOrden AS DECIMAL(20)) AS total_con_iva,
				ROUND(rog.valorOrden /1.08,0) AS antes_de_iva, ROUND(rog.valorOrden - (rog.valorOrden /1.08)) AS IVA,
				rog.fechaFactura as factura_fecha,  rog.idEstadoOrden AS estado_pedido,
				7777777 AS telefono_ciudad,
		 		SUBSTR(rog.observaciones,1,30) AS nota_cabecera1, -- Validar tamaños..
		 		SUBSTR(rog.observaciones,31,60) AS nota_cabecera2,  -- Validar tamaños..
		 		SUBSTR(rog.observaciones,61,90) AS nota_cabecera3,   -- Validar tamaños..
		 		'' AS transferencia1,
				'' AS transferencia2,
				'' AS transferencia3,
				-- 	SUBSTR(ap.referencia,1,30) AS transferencia1,
				--  SUBSTR(ap.referencia,31,60) AS transferencia2,
				--  SUBSTR(ap.referencia,61,90) AS transferencia3,
		 		 cpdv.resolucionDian as resolucion,CONVERT(cpdv.fechaResolucion, CHAR) AS fecha_resolucion,
		 		 cpdv.facturaInicial, cpdv.facturaFinal,rog.valorPropina AS propina,rod.valorTotal as precio,
				rog.idDomiciliario AS domiciliario,
		 		IF(tlu.nombre IS NULL, "SERVI COURIER",tlu.nombre ) AS nombre_domiciliario
 				FROM tcpr_ordengeneral rog
				INNER JOIN tcpr_ordendetalle rod ON rod.idOrdenGeneral = rog.idOrdenGeneral AND rog.idPunto=rod.idPunto
				INNER JOIN tcpc_puntoventa cpdv ON cpdv.idPunto = rog.idPunto
				INNER JOIN tcpc_ciudad gc ON rog.idCiudad= gc.idCiudad
				LEFT JOIN tcpc_loginusuarios tlu ON tlu.idUsuario = rog.idDomiciliario
				WHERE rog.idOrdenGeneral = _idOrdenGeneral
				ORDER BY rod.id;
        
				END IF;
		END ;;
