CREATE DEFINER=`integration`@`%` PROCEDURE `spcp_impresion_factura_tipoimpresion`(_idPunto INT, _idOrdenGeneral INT)
BEGIN
    DECLARE _retorna_factura INT;
    DECLARE _prefijo VARCHAR(5);
    DECLARE _imprimeautom INT;
    
      SELECT imprimeFacturaAuto INTO  _imprimeautom FROM tcpc_puntoventa WHERE idPunto = _idPunto;
     
 
    IF (_imprimeautom=1 OR _imprimeautom = 2 ) THEN  
				SELECT (facturaConsecutivo + 1) INTO _retorna_factura FROM tcpc_puntoventa WHERE idPunto=_idPunto FOR UPDATE; 
				UPDATE tcpc_puntoventa SET facturaConsecutivo = LAST_INSERT_ID(facturaConsecutivo + 1) WHERE idPunto = _idPunto; 
				SELECT prefijoFactura INTO _prefijo FROM tcpc_puntoventa WHERE idPunto= _idPunto;
				
				UPDATE tcpr_ordengeneral SET factura = _retorna_factura, fechaFactura=NOW()
				WHERE idOrdenGeneral = _idOrdenGeneral;
 
		SELECT _retorna_factura; 
	  END IF;
	 
    END ;;
