CREATE DEFINER=`integration`@`%` PROCEDURE `spcp_sil_cuadrecajamovil`(_idPunto INT,_fechaInicio VARCHAR(10),_fechaFin VARCHAR(10))
BEGIN
	#Routine body goes here...
	
	IF _idPunto = 0 THEN
			SELECT logu.nombre as NombreMovil,tpv.nombre as nombrePunto,ttp.nombre as nombreTipoPago,COUNT(tog.idOrdenGeneral) AS TotalOrdenes,SUM(tog.totalOrden) AS TotalVenta 
			FROM tcpr_ordengeneral tog 
			INNER JOIN  tcpc_puntoventa tpv ON tog.idPunto=tpv.idPunto
			INNER JOIN  tcpr_ordentipopago tfp ON tog.idOrdenGeneral=tfp.idOrdenGeneral
			INNER JOIN tcpc_tipopago ttp ON ttp.idTipoPago=tfp.idTipoPago
			INNER JOIN tcpc_loginusuarios logu ON logu.idUsuario = tog.idDomiciliario
			WHERE DATE(tog.fechaCrea) BETWEEN _fechaInicio AND _fechaFin AND tog.idEstadoOrden=2 -- AND tog.idDomiciliario =_idDomiciliario
			GROUP BY tfp.idTipoPago,tog.idPunto
			ORDER BY tog.idPunto;
			ELSE
				SELECT  logu.nombre as NombreMovil, tpv.nombre as nombrePunto,ttp.nombre as nombreTipoPago,COUNT(tog.idOrdenGeneral) AS TotalOrdenes,SUM(tog.totalOrden) AS TotalVenta 
				FROM tcpr_ordengeneral tog 
				INNER JOIN  tcpc_puntoventa tpv ON tog.idPunto=tpv.idPunto
				INNER JOIN  tcpr_ordentipopago tfp ON tog.idOrdenGeneral=tfp.idOrdenGeneral
				INNER JOIN tcpc_tipopago ttp ON ttp.idTipoPago=tfp.idTipoPago
				INNER JOIN tcpc_loginusuarios logu ON logu.idUsuario = tog.idDomiciliario
				WHERE DATE(tog.fechaCrea) BETWEEN _fechaInicio AND _fechaFin AND tog.idEstadoOrden=2 AND tog.idPunto=_idPunto -- AND tog.idDomiciliario =_idDomiciliario
				GROUP BY tfp.idTipoPago,tog.idPunto
				ORDER BY tog.idPunto;


END IF;

END ;;
