CREATE DEFINER=`integration`@`%` PROCEDURE `spcp_sil_cuadrecajapunto`(_nivel INT,_idPunto INT,_fechaInicio VARCHAR(10),_fechaFin VARCHAR(10))
BEGIN
	#Routine body goes here...

-- LCQ 13-06-2023= Nivel=1, si esta en cero, muestra todos los puntos para el admin, si trae valor en el punto, trae la venta del punto
IF _nivel = 1 THEN
	IF _idPunto = 0 THEN
			SELECT tog.idPunto,tpv.nombre as nombrePunto,COUNT(tog.idOrdenGeneral) AS TotalOrdenes,SUM(tog.totalOrden) AS TotalVenta 
			FROM tcpr_ordengeneral tog 
			INNER JOIN  tcpc_puntoventa tpv ON tog.idPunto=tpv.idPunto
			WHERE DATE(tog.fechaCrea) BETWEEN _fechaInicio AND _fechaFin AND tog.idEstadoOrden=2
			GROUP BY tog.idPunto;
			ELSE
				SELECT tog.idPunto,tpv.nombre as nombrePunto,COUNT(tog.idOrdenGeneral) AS TotalOrdenes,SUM(tog.totalOrden) AS TotalVenta 
				FROM tcpr_ordengeneral tog 
				INNER JOIN  tcpc_puntoventa tpv ON tog.idPunto=tpv.idPunto
				WHERE DATE(tog.fechaCrea) BETWEEN _fechaInicio AND _fechaFin AND tog.idEstadoOrden=2 AND tog.idPunto=_idPunto
				GROUP BY tog.idPunto;
END IF;
END IF;

-- LCQ 13-06-2023= Nivel=2, si esta en cero, muestra todas las formas de pago para el admin, si trae valor en el punto, trae las formas de pago de  venta del punto
IF _nivel = 2 THEN
	IF _idPunto = 0 THEN
			SELECT tpv.nombre as nombrePunto,tfp.idTipoPago,ttp.nombre as nombreTipoPago,COUNT(tog.idOrdenGeneral) AS TotalOrdenes,SUM(tog.totalOrden) AS TotalVenta 
			FROM tcpr_ordengeneral tog 
			INNER JOIN  tcpc_puntoventa tpv ON tog.idPunto=tpv.idPunto
			INNER JOIN  tcpr_ordentipopago tfp ON tog.idOrdenGeneral=tfp.idOrdenGeneral
			INNER JOIN tcpc_tipopago ttp ON ttp.idTipoPago=tfp.idTipoPago
			WHERE DATE(tog.fechaCrea) BETWEEN _fechaInicio AND _fechaFin AND tog.idEstadoOrden=2
			GROUP BY tfp.idTipoPago,tog.idPunto
			ORDER BY tog.idPunto;
			ELSE
				SELECT tpv.nombre as nombrePunto,tfp.idTipoPago,ttp.nombre as nombreTipoPago,COUNT(tog.idOrdenGeneral) AS TotalOrdenes,SUM(tog.totalOrden) AS TotalVenta 
				FROM tcpr_ordengeneral tog 
				INNER JOIN  tcpc_puntoventa tpv ON tog.idPunto=tpv.idPunto
				INNER JOIN  tcpr_ordentipopago tfp ON tog.idOrdenGeneral=tfp.idOrdenGeneral
				INNER JOIN tcpc_tipopago ttp ON ttp.idTipoPago=tfp.idTipoPago
				WHERE DATE(tog.fechaCrea) BETWEEN _fechaInicio AND _fechaFin AND tog.idEstadoOrden=2 AND tog.idPunto=_idPunto
				GROUP BY tfp.idTipoPago,tog.idPunto
				ORDER BY tog.idPunto;


END IF;
END IF;

END ;;
