CREATE DEFINER=`integration`@`%` PROCEDURE `spcp_sil_posarchivopunto`(_fechaInicio VARCHAR(10),_idPunto INT)
BEGIN
-- DECLARE Descripcion_Del_SP  text;
DECLARE _contador1 INT DEFAULT 0;
DECLARE _contador2 INT DEFAULT 0;
DECLARE _contador3 INT DEFAULT 0;
DECLARE _contador4 INT DEFAULT 0;
DECLARE _idordenDetalle INT;
DECLARE _id_OrdenGeneral INT;
DECLARE _idOrdenGeneral INT;
-- DECLARE _codigo_anterior VARCHAR(20) default '0';
DECLARE _skuItem VARCHAR(20);
DECLARE _skuItemPrincipal VARCHAR(20);
DECLARE _tipoItem VARCHAR(3);
DECLARE _registro1 text;
DECLARE _registro2 text;
DECLARE _registro3 text;
DECLARE _registro4 TEXT;
-- DECLARE _cadena_tot text;
-- DECLARE _ultimo_id INT DEFAULT 0;
DECLARE _idTipoPago INT DEFAULT 0;
DECLARE nomas1 INT; 
DECLARE _archivo TEXT;


	-- LCQ = 23-03-2023  Genera archivo plano para uso de sistemas uno85
   
	-- LCQ = 23-03-2023 Se crea el cursor para filtrar las ordenes activas del punto de venta	 
   DECLARE OrdenGeneral_cursor CURSOR FOR
			SELECT distinct tog.id,tog.idOrdenGeneral 
				FROM tcpr_ordengeneral tog 
				INNER JOIN tcpr_ordendetalle tod ON tog.idOrdenGeneral=tod.idOrdenGeneral
				INNER JOIN tcpr_ordentipopago totp ON tog.idOrdenGeneral=totp.idOrdenGeneral 
					WHERE LEFT(tog.fechaCrea,10) BETWEEN  _fechaInicio AND _fechaInicio 
						AND tog.idPunto = _idPunto
						AND tog.idEstadoOrden >= 2
						AND tog.idPunto NOT IN (9999,8888,7777)
						AND tog.factura IS NOT NULL;   		
							
	-- LCQ = 23-03-2023 Se crea el cursor para filtrar los items de las orden seleccionada				
   DECLARE ordenDetalle_cursor CURSOR FOR
		 SELECT tod.id,tod.skuItem,tod.skuItemPrincipal,tod.tipoItem 
			FROM tcpr_ordendetalle tod  
			WHERE tod.idOrdenGeneral =_idOrdenGeneral -- AND tod.skuItem NOT like'174'
			ORDER BY skuItemPrincipal, id;
	 
	-- LCQ = 23-03-2023 Se crea el cursor para filtrar la forma de pago de la orden	seleccionada		
   DECLARE ordenTipoPago_cursor CURSOR FOR
			SELECT id 
				FROM tcpr_ordentipopago totp  
				WHERE totp.idOrdenGeneral = _idOrdenGeneral
				ORDER BY id;
	
	-- LCQ = 23-03-2023 Se crea el cursor para filtrar una sola orden	
   DECLARE CONTINUE HANDLER FOR NOT FOUND SET nomas1 = 1;
	 
	 
-- LCQ = 23-03-2023 Se crea una tabla temporal para insertar el archivo	
DROP temporary TABLE IF EXISTS `uno85`;
		CREATE temporary TABLE  `uno85` (
		  `id` bigint(20) unsigned NOT NULL auto_increment,
		  `registro` text,
		  `contador` int(10) unsigned NOT NULL default '0',
		  `tipo` varchar(10) NOT NULL default '',
		  `fecha` datetime NOT NULL default '1981-01-07',
		  PRIMARY KEY  (`id`),
		  KEY `fecha` (`fecha`)
		) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='Tabla temporal para impresion';


-- LCQ = 23-03-2023 Se abre el cursor para filtrar uno a uno las ordenes	   
   OPEN OrdenGeneral_cursor;
					loop1: LOOP
						 FETCH OrdenGeneral_cursor INTO _id_OrdenGeneral,_idOrdenGeneral;
						 IF nomas1 = 1 THEN
								LEAVE loop1;
						 END IF;
						 
-- LCQ = 23-03-2023 Se abre el cursor para filtrar uno a uno los items de la orden 					 
   OPEN ordenDetalle_cursor;
					loop2: LOOP
						 FETCH ordenDetalle_cursor INTO _idordenDetalle,_skuItem,_skuItemPrincipal,_tipoItem;
						 IF nomas1 = 1 THEN
								SET nomas1 = 0;
								CLOSE ordenDetalle_cursor;							
								LEAVE loop2;
						 END IF;
   
-- LCQ = 23-03-2023 Se valida si el item es principal. 	
		IF UCASE(_tipoItem) = 'PRI' THEN
		
	-- LCQ = 23-03-2023 Se crea sentencia para el item principal. 	
	
			SELECT
				CONCAT('01',date_format(tog.fechaCrea,"%Y%m%d"),lpad(tpv.idPunto,3,'0'),lpad(tpv.cajaNumero,2,'0'),-- lpad(apc.dto_nro_lista,6,'0'),-- lpad(apc.dto_nro,6,'0'),
				RIGHT(tog.idOrdenGeneral,6),'F',tpv.prefijoFactura,lpad(tog.factura,6,'0'),-- lpad((_contador1+1),3,'0'), -- un numero secuencia de items se suma 1 porque contador comienza desde cero
				IF (tod.skuItem='174', 999,LPAD((_contador1+1),3,'0')), -- LCQ, se colocar esta condicion para que permitiera subir el recargo de forma correcta
				'R', -- Lcq= determina si el cargue de los productos se realizan por item o por referencia
				lpad('',15,' '),-- lpad('',15,' '),-- lpad(tod.skuItem,6,'0'),-- lpad('',3,' '),
				-- if(instr(tod.skuItem,'-') > 0,  lpad(substring_index(trim(tod.skuItem),'-',1),15,'0'), lpad(trim(tod.skuItem),15,'0') )  ,
				IF(INSTR(tod.skuItem,'-') > 0,  RPAD(SUBSTRING_INDEX(TRIM(tod.skuItem),'-',1),15,' '), RPAD(TRIM(tod.skuItem),15,' ') )  ,
				LPAD('',6,' '),
				IF(instr(tod.skuItem,'-') > 0,  rpad(substring_index(trim(tod.skuItem),'-',-1),3,'0'), rpad('',3,' ') ) ,
				lpad(tod.cantidad,9,'0'),'000+',
				'010', -- lcq= Numero de la lista de precio en siesa
				'  ',	'000000',
--  			if(isnull(tip.impuesto),lpad('',11,'0'),	lpad(cast(tod.valorBaseUni*100/(1 + (tip.impuesto/100)) as signed ),11,'0')),'+',
				lpad(cast(tod.valorBaseUni AS unsigned),9,'0'),'00+',
      			IF(isnull(tip.impuesto),lpad('',13,'0'),	lpad(cast(tod.valorTotal*100/(1 + (tip.impuesto/100)) as signed ),13,'0')),'+',
				'0000',
				'00000000000+',
 				IF(isnull(tip.impuesto),'00',lpad(cast(tip.impuesto as signed ),2,'0')),'00',
 				IF(isnull(tip.impuesto),lpad('',11,'0'),lpad(  cast(tod.valorTotal * 100 as unsigned )  - cast(tod.valorTotal*100/(1 + (tip.impuesto/100)) as signed ) ,11,'0')    ),'+', -- 23
				lpad(cast(tod.valorTotal * 100 as unsigned ),13,'0'),'+',  -- 24
				'06',  -- lcq= Numero del Motivo de la venta en siesa   -- 25
				lpad('',212,' '),  -- del campo 26 al 32
				date_format(tog.fechaCrea,'%H%i'), -- 34 ojo falta el 33
				IF(teo.idEstadoPos = 2,'X',' ')
				)				
				INTO _registro1
				FROM tcpr_ordengeneral tog
				LEFT JOIN tcpc_puntoventa tpv ON tpv.idPunto = tog.idPunto
				INNER JOIN tcpr_ordendetalle tod ON tod.idOrdenGeneral = tog.idOrdenGeneral
				INNER JOIN tcpc_estadoorden teo ON teo.idEstadoOrden = tog.idEstadoOrden
				LEFT JOIN tcpc_items tip ON tip.skuItem = tod.skuItem
				WHERE tod.id = _idordenDetalle
					 LIMIT 1;
					 
			SET _contador1 = _contador1 + 1;
			SET _contador3 =  _contador1;
			
			INSERT INTO uno85 (registro,contador,tipo,fecha) VALUES(_registro1,_contador1,_tipoItem,_fechaInicio);
	
		
		-- LCQ. 23-03-2023  = Cambia la posicion del utensilio para que salga como una seleccion en del rodeo,
			-- LCQ = 24-03-2023 Se crea sentencia para el item del utensilio que lleva el item principal. 	
		
			  IF EXISTS (	SELECT idItem FROM tcpc_items tip WHERE tip.skuItem = _skuItemPrincipal AND  tip.skuItemUtensilio != 0) THEN
				
				SELECT CONCAT('03',DATE_FORMAT(tog.fechaCrea,"%Y%m%d"),	LPAD(tpv.idPunto,3,'0'),LPAD(tpv.cajaNumero,2,'0'),	RIGHT(tog.idOrdenGeneral,6),
						LPAD((_contador3),3,'0'), -- toma el numero de secuencia del principal
						'I', -- Lcq= determina si el cargue de los productos se realizan por item o por referencia
						LPAD('',15,' '),LPAD('',15,' '),
						IF(INSTR(tip.skuItemUtensilio,'-') > 0,  LPAD(SUBSTRING_INDEX(TRIM(tip.skuItemUtensilio),'-',1),6,'0'), LPAD(TRIM(tip.skuItemUtensilio),6,'0') )  ,
						IF(INSTR(tip.skuItemUtensilio,'-') > 0,  RPAD(SUBSTRING_INDEX(TRIM(tip.skuItemUtensilio),'-',-1),3,'0'), RPAD('',3,' ') ) ,
						LPAD(tod.cantidad,9,'0'),'000+',
						IF(ISNULL(tiu.nombre),RPAD('Grupo No Encont',15,' '),RPAD(TRIM(LEFT(tiu.nombre,15)),15,' '))
						)
							INTO _registro4
						FROM tcpr_ordengeneral tog
						LEFT JOIN tcpc_puntoventa tpv ON tpv.idPunto = tog.idPunto
						INNER JOIN tcpr_ordendetalle tod ON tod.idOrdenGeneral = tog.idOrdenGeneral
						LEFT JOIN tcpc_items tip ON tip.skuItem = tod.skuItem
						LEFT JOIN tcpc_itemsutensilio tiu ON tip.skuItemUtensilio=tiu.skuItemUtensilio
						WHERE tip.skuItem = _skuItemPrincipal 
						 AND tod.id = _idordenDetalle
							LIMIT 1;

INSERT INTO uno85 (registro,contador,tipo,fecha) VALUES(_registro4,_contador3,'SEl',_fechaInicio);
		 END IF;		
		END IF;
	--  Hasta aquí termina la generación del item principal		
	

			-- LCQ = 24-03-2023 Se crea sentencia para el item seleción. 
		
		if UCASE(_tipoItem) = 'SEL' THEN
		
				SELECT concat('03',date_format(tog.fechaCrea,"%Y%m%d"),lpad(tpv.idPunto,3,'0'),lpad(tpv.cajaNumero,2,'0'),RIGHT(tog.idOrdenGeneral,6),
				lpad((_contador3),3,'0'), -- toma el numero de secuencia del principal
				'I', -- Lcq= determina si el cargue de los productos se realizan por item o por referencia
				lpad('',15,' '),lpad('',15,' '),
				if(instr(tod.skuItem,'-') > 0,  lpad(substring_index(trim(tod.skuItem),'-',1),6,'0'), lpad(trim(tod.skuItem),6,'0') )  ,
				if(instr(tod.skuItem,'-') > 0,  rpad(substring_index(trim(tod.skuItem),'-',-1),3,'0'), rpad('',3,' ') ) ,
				lpad(tod.cantidad,9,'0'),'000+',
				if(isnull(tigs.nombre),rpad('Grupo No Encont',15,' '),rpad(trim(left(tigs.nombre,15)),15,' '))
				)
				INTO _registro3			
			FROM tcpr_ordengeneral tog
						LEFT JOIN tcpc_puntoventa tpv ON tpv.idPunto = tog.idPunto
						INNER JOIN tcpr_ordendetalle tod ON tod.idOrdenGeneral = tog.idOrdenGeneral
						LEFT JOIN tcpr_itemseleccion tis ON tis.skuSeleccion = tod.skuItem						
						LEFT JOIN tcpr_itemsrelgruposeleccion tigr ON tigr.idItemGrupo = tis.idItemGrupo														
						LEFT JOIN tcpc_itemgruposeleccion tigs ON tigs.idItemGrupo = tis.idItemGrupo	
						WHERE ucase(tod.tipoItem) = 'SEL'	AND tigr.skuItem = _skuItemPrincipal AND tod.id = _idordenDetalle
						LIMIT 1;

			INSERT INTO uno85 (registro,contador,tipo,fecha) values(_registro3,_contador3,_tipoItem,_fechaInicio);
		END IF;
		 
 END LOOP loop2;
 
 
 
-- LCQ = 24-03-2023 Se aumentado para varios pagos
OPEN ordenTipoPago_cursor;
			loop3: LOOP
				 FETCH ordenTipoPago_cursor INTO _idTipoPago;
				 IF nomas1 = 1 THEN
						SET nomas1 = 0;
						CLOSE ordenTipoPago_cursor;
						LEAVE loop3;
				 END IF;
   
		-- LCQ = 24-03-2023 Se crea sentencia para sacar la formas de pago de la orden.  
   
	SELECT CONCAT('02',date_format(tog.fechaCrea,"%Y%m%d"),	lpad(tpv.idPunto,3,'0'),lpad(tpv.cajaNumero,2,'0'),	RIGHT(tog.idOrdenGeneral,6),
 		IF(isnull(tctp.tipoPos),'1',left(trim(tctp.tipoPos),1)),
 		IF(isnull(tctp.idPos),'02 ',rpad(trim(tctp.idPos),3,' ')),
		'I', -- Lcq= Indicador de ingreso/egreso del medio de recaudo efectivo.
		lpad('',4,' '), -- codigo entidad 9
		lpad('',6,' '), -- numero de cheque 10
		lpad('',20,' '), -- cuenta de cheque 11
		lpad('',8,' '), -- fecha de cheque 12
		IF(tctp.tipoPos = '3',LPAD(LEFT(tctp.nombre,10),10,' '),lpad('',10,' ')),
		lpad('',20,' '), --  numero de cuenta de medio otros 14
		lpad('',13,'0'),'+',  -- propina 15
		lpad('',13,'0'),'+',  -- iva 16
		lpad('',3,' '),  -- cta 17
		lpad('',6,' '),  -- consig 18
		IF(tctp.tipoPos = 'C','10','  '), -- codigo condicion medio de cartera 19
		IF(tctp.tipoPos = 'C',rpad(trim(tog.identificacion),13,' '),lpad('',13,' ')), -- Código del cliente de la factura del medio de recaudo cartera 20
		IF(tctp.tipoPos = 'C',lpad('',2,'0'),'  '), --  campos ,21 principal o sucursal = siempre 00 (ojo maestro.codigo_sucursal de 3 digitos,) o blancos para efectivo etc
--		lpad(cast(tog.totalOrden as signed ),15,'0'),'00+',
		lpad(cast(totp.valor AS signed ),15,'0'),'00+',
		lpad('',17,'0'),'+',  -- total modena extranjera 23
		lpad('',45,' ') --  campos 24 25
		)
			INTO _registro2			
		FROM tcpr_ordengeneral tog
		LEFT JOIN tcpc_puntoventa tpv ON tpv.idPunto = tog.idPunto
		LEFT JOIN tcpr_ordentipopago totp ON 	totp.idOrdenGeneral = tog.idOrdenGeneral
		LEFT JOIN tcpc_tipopago tctp ON tctp.idTipoPago = totp.idTipoPago
		WHERE tog.id = _id_OrdenGeneral
		AND totp.id = _idTipoPago;

		INSERT INTO uno85 (registro,contador,tipo,fecha) values(_registro2,_contador2,'fin ped',_fechaInicio);
			END LOOP loop3;
-- CLOSE ordenTipoPago_cursor;


		SET _contador2 = _contador2 + 1;
		SET _contador1 = 0;
		SET _contador3 = 0;
END LOOP loop1;
CLOSE OrdenGeneral_cursor;
-- Finalizan los Cursores para la orden;

/*SELECT json_object(
				"storeId",_idPunto,
				"archivo",
					(	SELECT  json_arrayagg(json_object(
								"id", ar.id,
								"registro", ar.registro,
								"contador", ar.contador,
								"tipo", ar.contador,
								"fecha", ar.fecha)))) AS archivo
								FROM uno85 ar;*/
								


	-- LCQ = 24-03-2023 Genera el archivo para guarda en el log.
		-- 	SELECT registro INTO _archivo FROM uno85;

	-- LCQ = 24-03-2023 Genera el archivo con todas las ordenes del punto.
	SELECT registro as Archivo FROM uno85;
			 


	-- LCQ = 24-03-2023 Crea un log del archivo generado por el punto.
		INSERT INTO tcpr_logarchivopos ( idPunto,archivo, fechaArchivo, fechaCrea)
				VALUES(_idPunto,'ArchivoGenerado',_fechaInicio,now());
		-- 	VALUES(_idPunto,_archivo,_fechaInicio,now());
		
END ;;
