CREATE DEFINER=`integration`@`%` PROCEDURE `spcp_sil_pqrpunto`(_nivel INT,_idPqrEncargado INT)
BEGIN
	#Routine body goes here...

-- LCQ = 13-06-2023 El nivel=1, Filtra los PQR abiertos o sin solucionar por el punto de venta
IF _nivel =1 THEN
SELECT tpg.fechaCrea AS FechaCaso,tpi.idPqr AS numeroCaso, tpe.nombre AS ResponsableEncargado, tpt.nombre AS Tipo, tptt.nombre AS Tema, tps.nombre AS Subtema,  tpi.descripcionIngreso as RequerimientoCliente,  
tpg.descripcionGestion AS Gestion_a_Realizar , tpg.descripcionRespuesta AS RespuestaResponsable,tpg.fechaRespuesta AS fechaRespuesta,trc.Nombre AS nombreCliente, trc.telefonoFijo as Telefono, trc.Direccion, tpi.idOrdenGeneral AS numeroOrden,tpg.idUsuario AS usuarioCaso,'Pendiente' AS estadoCaso, tpg.idPqrGestion
FROM tcpr_pqringreso tpi 
INNER JOIN tcpr_pqrgestion tpg ON tpi.idPqr = tpg.idPqr
INNER JOIN tcpr_cliente trc ON trc.idCliente = tpi.idCliente
INNER JOIN tcpc_pqrtipo tpt ON tpt.idPqrTipo = tpi.idPqrTipo
INNER JOIN tcpc_pqrtema tptt ON tptt.idPqrTema = tpi.idPqrTema
INNER JOIN tcpc_pqrsubtema tps ON tps.idPqrSubTema = tpi.idPqrSubTema
INNER JOIN tcpr_pqrencargado tpe ON tpe.idPqrEncargado = tpg.idPqrEncargado
WHERE  tpg.idPqrEncargado = _idPqrEncargado AND fechaRespuesta='1981-01-07 00:00:00' AND descripcionRespuesta=' '
ORDER BY tpg.fechaCrea DESC ; 

END IF;

-- LCQ = 13-06-2023 El nivel=2, Filtra los PQR cerrados o solucionados por el punto de venta
IF _nivel =2 THEN
SELECT tpg.fechaCrea AS FechaCaso,tpi.idPqr AS numeroCaso, tpe.nombre AS ResponsableEncargado, tpt.nombre AS Tipo, tptt.nombre AS Tema, tps.nombre AS Subtema,  tpi.descripcionIngreso as RequerimientoCliente,  
tpg.descripcionGestion AS Gestion_a_Realizar , tpg.descripcionRespuesta AS RespuestaResponsable,tpg.fechaRespuesta AS fechaRespuesta,trc.Nombre AS nombreCliente, trc.telefonoFijo as Telefono, trc.Direccion, tpi.idOrdenGeneral AS numeroOrden,tpg.idUsuario AS usuarioCaso,'Carrado' AS estadoCaso, tpg.idPqrGestion
FROM tcpr_pqringreso tpi 
INNER JOIN tcpr_pqrgestion tpg ON tpi.idPqr = tpg.idPqr
INNER JOIN tcpr_cliente trc ON trc.idCliente = tpi.idCliente
INNER JOIN tcpc_pqrtipo tpt ON tpt.idPqrTipo = tpi.idPqrTipo
INNER JOIN tcpc_pqrtema tptt ON tptt.idPqrTema = tpi.idPqrTema
INNER JOIN tcpc_pqrsubtema tps ON tps.idPqrSubTema = tpi.idPqrSubTema
INNER JOIN tcpr_pqrencargado tpe ON tpe.idPqrEncargado = tpg.idPqrEncargado
WHERE  tpg.idPqrEncargado = _idPqrEncargado AND fechaRespuesta!='1981-01-07 00:00:00' AND descripcionRespuesta!=' '
ORDER BY tpg.fechaCrea DESC ; 

END IF;


-- LCQ = 13-06-2023 El nivel=3, Muestra todos los PQR del punto de venta
IF _nivel =3 THEN
SELECT tpg.fechaCrea AS FechaCaso,tpi.idPqr AS numeroCaso, tpe.nombre AS ResponsableEncargado, tpt.nombre AS Tipo, tptt.nombre AS Tema, tps.nombre AS Subtema,  tpi.descripcionIngreso as RequerimientoCliente,  
tpg.descripcionGestion AS Gestion_a_Realizar , tpg.descripcionRespuesta AS RespuestaResponsable,tpg.fechaRespuesta AS fechaRespuesta,trc.Nombre AS nombreCliente, trc.telefonoFijo as Telefono, trc.Direccion, tpi.idOrdenGeneral AS numeroOrden,tpg.idUsuario AS usuarioCaso,IF(fechaRespuesta!='1981-01-07 00:00:00' AND descripcionRespuesta!=' ', 'Cerrado','Pendiente') AS estadoCaso, tpg.idPqrGestion
FROM tcpr_pqringreso tpi 
INNER JOIN tcpr_pqrgestion tpg ON tpi.idPqr = tpg.idPqr
INNER JOIN tcpr_cliente trc ON trc.idCliente = tpi.idCliente
INNER JOIN tcpc_pqrtipo tpt ON tpt.idPqrTipo = tpi.idPqrTipo
INNER JOIN tcpc_pqrtema tptt ON tptt.idPqrTema = tpi.idPqrTema
INNER JOIN tcpc_pqrsubtema tps ON tps.idPqrSubTema = tpi.idPqrSubTema
