CREATE DEFINER=`integration`@`%` PROCEDURE `spcp_sil_totaltrazas`(_codigo_restaurante INT, 
_codigo_traza INT)
BEGIN


IF _codigo_traza = 0 THEN
	SELECT tdl.idTraza, COUNT(tdl.idTraza) AS totaltraza
	FROM tcpr_trazasdia  tdl
	INNER JOIN  tcpr_ordengeneral c ON tdl.idOrdenGeneral = c.idOrdenGeneral 
	INNER JOIN (SELECT MAX(id) AS id FROM tcpr_trazasdia GROUP BY idOrdenGeneral) dtg1 ON dtg1.id=tdl.id
	WHERE LEFT(tdl.fechaInicio, 10) <= LEFT(NOW(),10)
	AND date(c.fechaCrea) = CURDATE() AND c.idEstadoOrden = 2
	AND tdl.fechaFin <= '1981-01-07 00:00:00';
ELSE
	SELECT tdl.idTraza, COUNT(tdl.idTraza) AS totaltraza
	FROM tcpr_trazasdia  tdl 
	INNER JOIN  tcpr_ordengeneral c ON tdl.idOrdenGeneral = c.idOrdenGeneral 
	INNER JOIN (SELECT MAX(id) AS id FROM tcpr_trazasdia group by idOrdenGeneral) dtg1 ON dtg1.id=tdl.id
	WHERE tdl.idTraza = _codigo_traza
	-- AND tdl.dialogos_codigo = id_campana
	AND LEFT(tdl.fechaInicio, 10) <= LEFT(NOW(),10)
	AND date(c.fechaCrea) = CURDATE() AND c.idEstadoOrden = 2
	AND tdl.fechaFin <= '1981-01-07 00:00:00' 
	GROUP BY tdl.idTraza;
	
END IF;

