CREATE DEFINER=`integration`@`%` PROCEDURE `spcp_website_georeferenciacionpunto`(_lng DOUBLE, 
  _lat DOUBLE,
  _idCiudad INT,
  _idMarca INT)
BEGIN
  IF EXISTS (SELECT idPoligono FROM tcpr_poligonos WHERE x1 > x2 ) 
    THEN
        DROP TABLE IF EXISTS `tcpr_poligonos_tempo`; 
        CREATE TEMPORARY TABLE `tcpr_poligonos_tempo` AS SELECT * FROM tcpr_poligonos WHERE x1 > x2; 
        UPDATE tcpr_poligonos_tempo dpt , tcpr_poligonos dp SET dp.x1 = dpt.x2, dp.y1 = dpt.y2, dp.x2 = dpt.x1, dp.y2 = dpt.y1 WHERE dp.idPoligono = dpt.idRectangulo; 
		TRUNCATE TABLE tcpr_rectangulos;
		INSERT INTO tcpr_rectangulos (idPunto, x1, y1, x2, y2)
		SELECT idPunto, MIN(x1),MIN(y1),MAX(x2),MAX(y2) FROM tcpr_poligonos GROUP BY idPunto;
	END IF;
     
    
SELECT geo.idPunto,geo.direccion,geo.pedidoMinimo,geo.costoEnvio,geo.telefonoCelular,horaInicio,horaFin,geo.listaPrecio,geo.dentro
FROM
(SELECT r.idPunto,tpv.direccion,tpv.pedidoMinimo,tpv.costoEnvio,telefonoCelular,thp.inicio AS horaInicio,thp.fin AS horaFin,tpv.listaPrecio,
 SUM(IF(_lng  >= (_lat * p.x2 - _lat * p.x1 - p.x2*p.y1 + p.x1*p.y2)/(p.y2 - p.y1),IF((_lat * p.x2 - _lat * p.x1 - p.x2*p.y1 + p.x1*p.y2)/(p.y2 - p.y1) >=p.x1, 
IF( (_lat * p.x2 - _lat * p.x1 - p.x2*p.y1 + p.x1*p.y2)/(p.y2 - p.y1) <=p.x2  ,1,0),0)  ,0))  MOD 2 AS dentro 
FROM tcpr_rectangulos r
INNER JOIN tcpr_poligonos p ON p.idPunto = r.idPunto
INNER JOIN tcpc_puntoventa tpv ON tpv.idPunto=r.idPunto
INNER JOIN tcpr_horariospuntoventa thp ON thp.idPunto=r.idPunto AND thp.dia=DAYOFWEEK(now())
WHERE _lng >= r.x1 AND _lng <= r.x2 AND _lat >= r.y1 AND _lat <= r.y2
AND p.idCiudad = _idCiudad
AND p.idMarca = _idMarca
GROUP BY p.idPunto,thp.inicio,thp.fin) geo
WHERE geo.dentro=1;
END ;;
