CREATE DEFINER=`integration`@`%` PROCEDURE `spcp_website_itemseleccion`(_idPunto INT,_skuItem VARCHAR(20))
BEGIN

DECLARE _listaPrecio INT;


SELECT listaPrecio INTO _listaPrecio FROM tcpc_puntoventa WHERE idPunto = _idPunto;

SELECT prod.skuItem,-- prod.itemNombre,pp.precio as itemPrecio,
gru.idItemGrupo AS idGrupoSeleccion, 
				gru.nombre as nombreGrupoSeleccion, opc.skuItem AS skuItemSeleccion,
				opc.itemNombre as ItemSeleccion, 
				IF(pre.precio IS NULL, 0,pre.precio) AS precioSeleccion,
				relopc.tipoOpcion AS tipoSeleccion ,
				relopc.porDefecto AS SeleccionPorDefecto, 
				relgru.cantidadMaxima AS cantidadMaxima, prod.descripcion	as 	descripcionProducto		
				FROM tcpc_items prod
				INNER  JOIN tcpr_itemslistaprecio pp ON prod.skuItem = pp.skuItem				
				LEFT JOIN tcpr_itemsrelgruposeleccion relgru on prod.skuItem = relgru.skuItem				
				LEFT JOIN tcpc_itemgruposeleccion gru on relgru.idItemGrupo = gru.idItemGrupo				
				LEFT JOIN tcpr_itemseleccion relopc on relopc.idItemGrupo = relgru.idItemGrupo				
				LEFT JOIN tcpc_items opc on opc.skuItem = relopc.skuSeleccion
				LEFT JOIN tcpr_itemslistaprecio pre on opc.skuItem = pre.skuItem
				WHERE prod.skuItem LIKE _skuItem 
				AND pp.listaPrecio = _listaPrecio 
				AND (relopc.activo =1 or relopc.activo is null) order by gru.idItemGrupo, opc.itemNombre;




END ;;
